// Create or Update Order Production Status Records
const orderGroups = $('Group Ready Items').all()[0].json.orderGroups || new Map();
const config = $('Batch Configuration').first().json;
const PRODUCTION_RATES = config.PRODUCTION_RATES;

// Get existing Order Production Status records
const statusTable = base.getTable('Order Production Status');
const existingStatuses = await statusTable.selectRecordsAsync({
  fields: ['Order ID']
});

const existingOrderIds = new Set(
  existingStatuses.records.map(r => r.getCellValue('Order ID')?.[0]?.id)
);

// Calculate production estimates
function estimateProductionTime(items) {
  let totalTime = PRODUCTION_RATES.Setup || 15;

  items.forEach(item => {
    const productType = item['Normalized Product Type'] || 'DTF';
    const quantity = item.Quantity || 1;
    const rate = PRODUCTION_RATES[productType] || 2;
    totalTime += quantity * rate;
  });

  return totalTime;
}

// Create updates for each order
const statusUpdates = [];

for (const [orderId, orderData] of orderGroups) {
  const totalItems = orderData.items.length;
  const itemsQueued = orderData.items.filter(i => i['Item Status'] === 'Queued').length;
  const itemsInProduction = orderData.items.filter(i => i['Item Status'] === 'In Production').length;
  const itemsCompleted = orderData.items.filter(i => i['Item Status'] === 'Completed').length;

  const completionPercent = Math.round((itemsCompleted / totalItems) * 100);
  const estimatedMinutes = estimateProductionTime(orderData.items);
  const estimatedCompletion = new Date(Date.now() + estimatedMinutes * 60000);

  const statusData = {
    'Order ID': [{id: orderId}],
    'Total Items': totalItems,
    'Items Queued': itemsQueued,
    'Items In Production': itemsInProduction,
    'Items Completed': itemsCompleted,
    'Completion %': completionPercent,
    'Estimated Completion': estimatedCompletion,
    'Status': itemsCompleted === totalItems ? 'Completed' : 
              itemsInProduction > 0 ? 'In Production' : 
              itemsQueued > 0 ? 'Queued' : 'Awaiting Batch',
    'Last Updated': new Date()
  };

  if (existingOrderIds.has(orderId)) {
    // Update existing
    statusUpdates.push({
      type: 'update',
      orderId: orderId,
      data: statusData
    });
  } else {
    // Create new
    statusUpdates.push({
      type: 'create',
      data: statusData
    });
  }
}

return statusUpdates;

// Recalculate Queue Positions for All Active Batches
const config = $('Batch Configuration').first().json;
const QUEUE_SETTINGS = config.QUEUE_SETTINGS;
const PRODUCTION_RATES = config.PRODUCTION_RATES;

// Get existing batches
const existingBatches = $input.all().map(item => item.json);

// Calculate dynamic priority with age bonus
function calculateDynamicPriority(batch) {
  const now = new Date();
  const created = new Date(batch.Created);
  const ageInHours = (now - created) / (1000 * 60 * 60);
  const ageBonus = Math.min(ageInHours * QUEUE_SETTINGS.AGE_BONUS_PER_HOUR, QUEUE_SETTINGS.MAX_AGE_BONUS);

  // Base priority scores
  let basePriority = 0;
  if (batch.Priority === 'Urgent') basePriority = 10000;
  else if (batch.Priority === 'High') basePriority = 5000;
  else basePriority = 1000;

  return basePriority + ageBonus;
}

// Estimate production time for a batch
function estimateProductionTime(batch) {
  const quantity = batch['Total Quantity'] || 0;
  const productType = batch['Print Type'] || 'DTF';
  const rate = PRODUCTION_RATES[productType] || PRODUCTION_RATES['DTF'];
  return PRODUCTION_RATES.Setup + (quantity * rate);
}

// Separate batches by status and facility
const batchesByFacility = {
  'CHICO': { inProgress: [], queued: [] },
  'OUTPOST': { inProgress: [], queued: [] }
};

existingBatches.forEach(batch => {
  const status = batch['Batch Status'];
  const facility = batch.Facility || (batch['Batch Name']?.includes('CHICO') ? 'CHICO' : 'OUTPOST');

  const batchData = {
    id: batch.id,
    name: batch['Batch Name'],
    priority: batch.Priority,
    created: batch.Created,
    quantity: batch['Total Quantity'],
    productType: batch['Print Type'],
    dynamicPriority: calculateDynamicPriority(batch),
    estimatedTime: estimateProductionTime(batch),
    currentPosition: batch['Queue Position']
  };

  if (status === 'Printing' || status === 'In Progress') {
    batchesByFacility[facility].inProgress.push(batchData);
  } else if (status === 'Created' || status === 'Queued') {
    batchesByFacility[facility].queued.push(batchData);
  }
});

// Process each facility separately
const updates = [];

Object.entries(batchesByFacility).forEach(([facility, batches]) => {
  // Sort queued batches by dynamic priority
  batches.queued.sort((a, b) => b.dynamicPriority - a.dynamicPriority);

  // Calculate positions and times
  let position = batches.inProgress.length + 1;
  let cumulativeTime = batches.inProgress.reduce((sum, b) => sum + b.estimatedTime, 0);

  batches.queued.forEach(batch => {
    const estimatedStart = new Date(Date.now() + cumulativeTime * 60000);
    const estimatedCompletion = new Date(estimatedStart.getTime() + batch.estimatedTime * 60000);

    updates.push({
      id: batch.id,
      fields: {
        'Queue Position': position,
        'Estimated Start Time': estimatedStart.toISOString(),
        'Estimated Completion Time': estimatedCompletion.toISOString(),
        'Dynamic Priority Score': Math.round(batch.dynamicPriority)
      }
    });

    position++;
    cumulativeTime += batch.estimatedTime;
  });
});

console.log(`Queue Recalculation Summary:`);
console.log(`- CHICO: ${batchesByFacility.CHICO.inProgress.length} in progress, ${batchesByFacility.CHICO.queued.length} queued`);
console.log(`- OUTPOST: ${batchesByFacility.OUTPOST.inProgress.length} in progress, ${batchesByFacility.OUTPOST.queued.length} queued`);
console.log(`- Total updates: ${updates.length}`);

return updates;

// Prepare Order Items Update with Batch IDs
const batchesCreated = $('Create Batches in Airtable').all();
const itemUpdates = [];

// Log to debug what we're getting
console.log(`Processing ${batchesCreated.length} batch responses`);

// Process all batches (whether created or updated)
for (let i = 0; i < batchesCreated.length; i++) {
  const batch = batchesCreated[i];

  // Airtable upsert returns the record with id and fields
  // The structure should be: { id: "rec...", fields: { ... }, createdTime: "..." }

  if (!batch.json || !batch.json.id) {
    console.log(`Batch ${i} has no valid ID, skipping`);
    continue;
  }

  const batchId = batch.json.id;
  const fields = batch.json.fields || {};

  // Get the batch details we need
  const batchName = fields['Batch Name'];
  const queuePosition = fields['Queue Position'];
  const linkedOrderItems = fields['Linked Order Item'] || [];

  console.log(`Batch: ${batchName} (${batchId}) has ${linkedOrderItems.length} linked items`);

  // Update each linked order item
  if (linkedOrderItems.length > 0) {
    for (const itemId of linkedOrderItems) {
      itemUpdates.push({
        json: {
          id: itemId,
          fields: {
            'Batch ID': [batchId],  // Array since it's a linked record field
            'Item Status': 'In Production',
            'Batch Name': batchName,
            'Queue Position': queuePosition,
            'Last Updated': new Date().toISOString()
          }
        }
      });
    }
  }
}

// Return results
if (itemUpdates.length === 0) {
  console.log('No order items found to update');
  return [{
    json: {
      message: 'No items to update',
      batchesProcessed: batchesCreated.length,
      timestamp: new Date().toISOString(),
      debug: batchesCreated.length > 0 ? JSON.stringify(batchesCreated[0].json, null, 2) : 'No batches'
    }
  }];
}

console.log(`Prepared ${itemUpdates.length} order items for update`);
return itemUpdates;

