// Check for Behind Schedule Orders
const orderStatusUpdates = $('Update Order Status').all();
const now = new Date();
const behindSchedule = [];

orderStatusUpdates.forEach(update => {
  const data = update.json.data;
  const estimatedCompletion = new Date(data['Estimated Completion']);
  const completionPercent = data['Completion %'] || 0;

  // Check if behind schedule
  if (estimatedCompletion < now && completionPercent < 100) {
    const hoursLate = Math.round((now - estimatedCompletion) / (1000 * 60 * 60));

    behindSchedule.push({
      orderId: data['Order ID'][0].id,
      orderNumber: data['Order ID'][0].name || 'Unknown',
      estimatedCompletion: estimatedCompletion,
      hoursLate: hoursLate,
      completionPercent: completionPercent,
      status: data['Status'],
      itemsCompleted: data['Items Completed'],
      totalItems: data['Total Items']
    });
  }
});

if (behindSchedule.length > 0) {
  return [{
    json: {
      behindScheduleCount: behindSchedule.length,
      orders: behindSchedule
    }
  }];
} else {
  return [{
    json: {
      message: "All orders on schedule",
      behindScheduleCount: 0
    }
  }];
}

