// Enhanced Auto-Assignment with Priority Scoring
const config = $('Batch Configuration').first().json;
const PRIORITY_SCORES = config.PRIORITY_SCORES;
const FACILITY_RULES = config.FACILITY_RULES;
const PRODUCT_TYPE_MAPPING = config.PRODUCT_TYPE_MAPPING;
const NON_PRINT_PRODUCTS = config.NON_PRINT_PRODUCTS || [];
const MANUAL_ASSIGNMENT_PRODUCTS = config.MANUAL_ASSIGNMENT_PRODUCTS || [];

// Get items and orders
const itemsData = $('Get Order Details').first().json.items;
const orders = $input.all();

// Create order map
const orderMap = new Map(orders.map(o => [o.json.id, o.json]));

// Helper function to normalize product type
function normalizeProductType(productType) {
  if (!productType) return 'DTF';

  // Check mapping
  for (const [key, value] of Object.entries(PRODUCT_TYPE_MAPPING)) {
    if (key.toLowerCase() === productType.toLowerCase()) {
      return value;
    }
  }

  // Fallback pattern matching
  const lowerProduct = productType.toLowerCase();
  if (lowerProduct.includes('uv')) {
    return lowerProduct.includes('gang') || lowerProduct.includes('sheet') ? 'UV Gang Sheet' : 'UV';
  }
  if (lowerProduct.includes('dtf')) {
    return lowerProduct.includes('gang') || lowerProduct.includes('sheet') ? 'DTF Gang Sheet' : 'DTF';
  }
  if (lowerProduct.includes('sub')) return 'Sublimation';
  if (lowerProduct.includes('heat') && lowerProduct.includes('press')) return 'Heat Press';

  return 'DTF'; // default
}

// Enhanced auto-assignment logic using configuration
const assignedItems = itemsData.map(item => {
  const order = orderMap.get(item['Linked Order']?.[0]);
  if (!order) return item;

  const source = (order.Source || '').toLowerCase();
  const originalProductType = item['Product Type'] || 'DTF Transfers';
  const productType = normalizeProductType(originalProductType);

  // Check if this is a non-print product
  const isNonPrint = NON_PRINT_PRODUCTS.includes(productType);
  const needsManualProductAssignment = MANUAL_ASSIGNMENT_PRODUCTS.includes(productType);

  // Use facility rules from configuration
  let facility = item['Print Facility'] || null;
  let printerAssignment = item['Printer Assignment'] || null;

  // Only assign facility/printer if not a non-print product
  if (!isNonPrint && FACILITY_RULES[source] && FACILITY_RULES[source][productType]) {
    const rule = FACILITY_RULES[source][productType];
    facility = rule.facility || facility;
    printerAssignment = rule.printer || printerAssignment;
  }

  // Calculate priority score using configuration
  const scoreBreakdown = {
    base: PRIORITY_SCORES.BASE,
    source: 0,
    shipping: 0,
    rushService: 0,
    features: 0,
    tags: 0,
    priorityLevel: 0,
    forceBonus: 0
  };

  // Source priority
  scoreBreakdown.source = PRIORITY_SCORES.SOURCES[source] || 0;

  // Shipping method priority
  const shipping = (order['Shipping Method'] || '').toLowerCase();
  Object.entries(PRIORITY_SCORES.SHIPPING).forEach(([method, points]) => {
    if (shipping.includes(method.replace('_', ' '))) {
      scoreBreakdown.shipping = Math.max(scoreBreakdown.shipping, points);
    }
  });

  // Rush service
  const rushService = order['Rush Service'] || '';
  scoreBreakdown.rushService = PRIORITY_SCORES.RUSH_SERVICE[rushService] || 0;

  // Features
  if (order['Pre-cut?']) scoreBreakdown.features += PRIORITY_SCORES.FEATURES.pre_cut;
  if (order['Gang Sheet Required']) scoreBreakdown.features += PRIORITY_SCORES.FEATURES.gang_sheet_required;

  // Order tags
  const tags = order['Order Tags'] || [];
  if (Array.isArray(tags)) {
    tags.forEach(tag => {
      const tagLower = tag.toLowerCase();
      Object.entries(PRIORITY_SCORES.TAGS).forEach(([tagKey, points]) => {
        if (tagLower.includes(tagKey.replace('-', ' '))) {
          scoreBreakdown.tags += points;
        }
      });
    });
  }

  // Priority level
  const priorityLevel = order['Priority Level'] || '';
  scoreBreakdown.priorityLevel = PRIORITY_SCORES.PRIORITY_LEVELS[priorityLevel] || 0;

  // Force Batch
  if (order['Force Batch']) {
    scoreBreakdown.forceBonus = PRIORITY_SCORES.FORCE_BATCH;
  }

  // Calculate total score
  const totalScore = Object.values(scoreBreakdown).reduce((sum, score) => sum + score, 0);

  // Determine if needs manual assignment
  const needsManualAssignment = !isNonPrint && (
    !facility || 
    (facility === 'OUTPOST' && !printerAssignment) ||
    needsManualProductAssignment
  );

  // Update item with assignments and score breakdown
  return {
    ...item,
    'Product Type': originalProductType,
    'Normalized Product Type': productType,
    'Print Facility': facility,
    'Printer Assignment': printerAssignment,
    orderData: order,
    orderId: order.id,
    needsManualAssignment: needsManualAssignment,
    isNonPrint: isNonPrint,
    priorityScore: totalScore,
    scoreBreakdown: scoreBreakdown,
    forceBatch: order['Force Batch'] || false
  };
});

// Return individual items
return assignedItems.map(item => ({
  json: item
}));

