// ORDER-CENTRIC BATCHING - Groups items by order first
const config = $('Batch Configuration').first().json;
const ORDER_GROUPING = config.ORDER_GROUPING;
const BATCH_THRESHOLDS = config.BATCH_THRESHOLDS;
const NON_PRINT_PRODUCTS = config.NON_PRINT_PRODUCTS || [];

// Get all items
const items = $input.all().map(item => item.json);

// Filter out items that need manual assignment AND non-print products
const readyItems = items.filter(item => 
  !item.needsManualAssignment && 
  !item.isNonPrint &&
  !NON_PRINT_PRODUCTS.includes(item['Normalized Product Type'] || item['Product Type'])
);

// GROUP BY ORDER FIRST
const orderGroups = new Map();

readyItems.forEach(item => {
  const orderId = item.orderId || item['Linked Order']?.[0];
  if (!orderId) return;

  if (!orderGroups.has(orderId)) {
    orderGroups.set(orderId, {
      orderId: orderId,
      items: [],
      facilities: new Set(),
      productTypes: new Set(),
      highestPriority: 0,
      totalQuantity: 0,
      forceBatch: false,
      orderData: item.orderData
    });
  }

  const group = orderGroups.get(orderId);
  group.items.push(item);
  group.facilities.add(item['Print Facility'] || 'UNASSIGNED');
  group.productTypes.add(item['Normalized Product Type'] || item['Product Type']);
  group.highestPriority = Math.max(group.highestPriority, item.priorityScore || 0);
  group.totalQuantity += item.Quantity || 0;
  group.forceBatch = group.forceBatch || item.forceBatch;
});

// DETERMINE BATCHING STRATEGY FOR EACH ORDER
const readyGroups = [];
const pendingGroups = [];

for (const [orderId, orderGroup] of orderGroups) {
  // Find the appropriate threshold
  let requiredCount = 20;
  for (const threshold of BATCH_THRESHOLDS) {
    if (orderGroup.highestPriority >= threshold.minScore) {
      requiredCount = threshold.threshold;
      break;
    }
  }

  // Strategy 1: Keep order together if possible
  if (orderGroup.facilities.size === 1 && orderGroup.totalQuantity <= ORDER_GROUPING.MAX_BATCH_SIZE) {
    // Single facility, reasonable size - keep together
    readyGroups.push({
      key: `ORDER_${orderId}`,
      items: orderGroup.items,
      productType: orderGroup.productTypes.size > 1 ? 'Mixed' : Array.from(orderGroup.productTypes)[0],
      facility: Array.from(orderGroup.facilities)[0],
      priorityLevel: getPriorityLevel(orderGroup.highestPriority),
      priorityScore: orderGroup.highestPriority,
      isForced: orderGroup.forceBatch,
      batchReason: orderGroup.forceBatch ? 'Force Batched' : 'Order Kept Together',
      isFullOrder: true,
      mixedTypes: orderGroup.productTypes.size > 1,
      orderId: orderId
    });
  }
  // Strategy 2: Split by facility if multiple facilities
  else if (orderGroup.facilities.size > 1) {
    // Group by facility
    const facilityGroups = new Map();

    orderGroup.items.forEach(item => {
      const facility = item['Print Facility'] || 'UNASSIGNED';
      if (!facilityGroups.has(facility)) {
        facilityGroups.set(facility, []);
      }
      facilityGroups.get(facility).push(item);
    });

    // Create batch for each facility
    for (const [facility, facilityItems] of facilityGroups) {
      readyGroups.push({
        key: `ORDER_${orderId}_${facility}`,
        items: facilityItems,
        productType: 'Mixed',
        facility: facility,
        priorityLevel: getPriorityLevel(orderGroup.highestPriority),
        priorityScore: orderGroup.highestPriority,
        isForced: orderGroup.forceBatch,
        batchReason: 'Split by Facility',
        isFullOrder: false,
        partialOrder: true,
        orderId: orderId
      });
    }
  }
  // Strategy 3: Large order - may need to split
  else if (orderGroup.totalQuantity > ORDER_GROUPING.MAX_BATCH_SIZE && ORDER_GROUPING.SPLIT_LARGE_ORDERS) {
    // Split large orders into chunks
    const chunks = [];
    let currentChunk = [];
    let currentSize = 0;

    orderGroup.items.forEach(item => {
      const itemQty = item.Quantity || 0;
      if (currentSize + itemQty > ORDER_GROUPING.MAX_BATCH_SIZE && currentChunk.length > 0) {
        chunks.push(currentChunk);
        currentChunk = [];
        currentSize = 0;
      }
      currentChunk.push(item);
      currentSize += itemQty;
    });

    if (currentChunk.length > 0) {
      chunks.push(currentChunk);
    }

    chunks.forEach((chunk, index) => {
      readyGroups.push({
        key: `ORDER_${orderId}_CHUNK_${index + 1}`,
        items: chunk,
        productType: 'Mixed',
        facility: Array.from(orderGroup.facilities)[0],
        priorityLevel: getPriorityLevel(orderGroup.highestPriority),
        priorityScore: orderGroup.highestPriority,
        isForced: orderGroup.forceBatch,
        batchReason: `Large Order Chunk ${index + 1}/${chunks.length}`,
        isFullOrder: false,
        largeOrder: true,
        orderId: orderId
      });
    });
  }
  // Strategy 4: Not enough items yet
  else if (orderGroup.items.length < requiredCount && !orderGroup.forceBatch) {
    pendingGroups.push({
      key: `ORDER_${orderId}`,
      orderGroup,
      requiredCount,
      needsMore: requiredCount - orderGroup.items.length
    });
  }
}

function getPriorityLevel(score) {
  if (score >= 50000) return 'Force Batch';
  if (score >= 10000) return 'Super Rush';
  if (score >= 9000) return 'Overnight';
  if (score >= 8000) return 'Jiffy Priority';
  if (score >= 7000) return 'Rush 1-2 Days';
  if (score >= 6000) return 'Express';
  if (score >= 5000) return 'Priority';
  if (score >= 4000) return 'High Priority';
  return 'Normal';
}

