// Calculate dynamic priority
function calculateDynamicPriority(batch) {
  let baseScore = batch.priorityScore;

  // For existing batches without priorityScore
  if (!batch.priorityScore && batch.priority) {
    if (batch.priority === 'Urgent') baseScore = 10000;
    else if (batch.priority === 'High') baseScore = 5000;
    else baseScore = 1000;
  }

  // Calculate age bonus
  const now = new Date();
  const created = new Date(batch.created);
  const ageInHours = (now - created) / (1000 * 60 * 60);
  const ageBonus = Math.min(ageInHours * QUEUE_SETTINGS.AGE_BONUS_PER_HOUR, QUEUE_SETTINGS.MAX_AGE_BONUS);

  return baseScore + ageBonus;
}

