// Aggregate Production by Order and Update Order Production Status
const productionRecords = $('Get Production for Sync').all();
const orderAggregates = new Map();

// Group by order
for (const record of productionRecords) {
  const orderId = record.json.fields?.['Linked Order']?.[0];
  if (!orderId) continue;

  if (!orderAggregates.has(orderId)) {
    orderAggregates.set(orderId, {
      orderId,
      totalItems: 0,
      itemsCompleted: 0,
      itemsInProgress: 0,
      itemsQueued: 0,
      totalProgress: 0,
      hasOverdue: false,
      highestPriority: 'Standard'
    });
  }

  const agg = orderAggregates.get(orderId);
  const status = record.json.fields?.['Production Status'];
  const progress = record.json.fields?.['Stage Progress'] || 0;
  const priority = record.json.fields?.['Priority'];
  const isOverdue = record.json.fields?.['Is Overdue'];

  agg.totalItems++;
  agg.totalProgress += progress;

  // Count by status
  if (status === 'Completed') {
    agg.itemsCompleted++;
  } else if (status === 'In Progress' || status === 'Quality Check') {
    agg.itemsInProgress++;
  } else if (status === 'Pending') {
    agg.itemsQueued++;
  }

  // Track overdue
  if (isOverdue) agg.hasOverdue = true;

  // Track highest priority
  if (priority === 'Urgent' || agg.highestPriority !== 'Urgent') {
    if (priority === 'Urgent' || (priority === 'High' && agg.highestPriority === 'Standard')) {
      agg.highestPriority = priority;
    }
  }
}

// Convert to Order Production Status updates
const updates = [];
for (const [orderId, data] of orderAggregates) {
  const completionPercent = Math.round((data.totalProgress / data.totalItems) / 100 * 100);

  let status = 'Queued';
  if (data.itemsCompleted === data.totalItems) {
    status = 'Completed';
  } else if (data.itemsInProgress > 0) {
    status = 'In Production';
  }

  updates.push({
    orderId,
    fields: {
      'Total Items': data.totalItems,
      'Items Queued': data.itemsQueued,
      'Items In Production': data.itemsInProgress,
      'Items Completed': data.itemsCompleted,
      'Completion %': completionPercent,
      'Status': status,
      'Behind Schedule': data.hasOverdue,
      'Last Updated': new Date().toISOString()
    }
  });
}

return updates.map(update => ({ json: update }));
