// Enhanced Source Detection and Shipment Preparation
const items = $input.all();

return items.map(item => {
  const d = item.json;

  // More robust source detection with additional checks
  let source = 'unknown';
  let shipmentData = {};

  // Shopify Detection
  if (d.line_items && Array.isArray(d.line_items)) {
    source = 'shopify';

    // Extract Shopify-specific shipment data
    shipmentData = {
      orderId: d.name || d.id,
      orderNumber: d.order_number,
      fulfillmentStatus: d.fulfillment_status,
      shippingAddress: d.shipping_address,
      customer: {
        email: d.email || d.customer?.email,
        name: d.shipping_address?.name || `${d.customer?.first_name} ${d.customer?.last_name}`,
        phone: d.shipping_address?.phone || d.customer?.phone
      },
      items: d.line_items?.map(item => ({
        name: item.name,
        quantity: item.quantity,
        sku: item.sku,
        variant: item.variant_title
      })),
      financial: {
        subtotal: parseFloat(d.subtotal_price || 0),
        shipping: parseFloat(d.total_shipping_price_set?.shop_money?.amount || 0),
        tax: parseFloat(d.total_tax || 0),
        total: parseFloat(d.total_price || 0)
      }
    };
  }

  // JotForm Detection
  else if (d.answers && typeof d.answers === 'object') {
    source = 'jotform';

    // Extract JotForm-specific shipment data
    shipmentData = {
      orderId: d.submissionID || d.id,
      submissionId: d.submissionID,
      customer: {
        email: d.answers?.email?.answer || d.email,
        name: d.answers?.name?.answer || '',
        phone: d.answers?.phone?.answer || ''
      },
      shippingAddress: {
        // Parse from JotForm address fields if available
        name: d.answers?.shippingName?.answer || d.answers?.name?.answer,
        address1: d.answers?.address?.answer?.addr_line1,
        address2: d.answers?.address?.answer?.addr_line2,
        city: d.answers?.address?.answer?.city,
        state: d.answers?.address?.answer?.state,
        zip: d.answers?.address?.answer?.postal,
        country: d.answers?.address?.answer?.country || 'US'
      },
      items: [], // Would need to parse from form answers
      financial: {
        total: parseFloat(d.answers?.total?.answer || 0)
      }
    };
  }

  // Jiffy Email Detection (enhanced with your preprocessing data)
  else if (d.orderId && d.orderId.startsWith('JIFFY-')) {
    source = 'jiffy';

    // Use the preprocessed Jiffy data structure
    shipmentData = {
      orderId: d.orderId,
      poNumber: d.poNumber || d.jiffyPoNumber,
      orderType: d.orderType,
      rushService: d.rushService,
      customer: d.customer,
      shippingAddress: d.shipping,
      shippingDate: d.shippingDate,
      items: d.items,
      financial: d.financial,

      // Critical shipping label info
      shippingLabel: d.shippingLabel,
      shipping_label_url: d.shipping_label_url || d.shipping_label_google_drive,

      // File information
      files: d.files,
      gangSheetUrls: d.gangSheetUrls,
      cutlineReferences: d.cutlineReferences,  // ADDED: Cutline references

      // Status
      paymentStatus: d.paymentStatus,
      fileStatus: d.fileStatus
    };
  }

  // Legacy Jiffy detection (for emails not yet preprocessed)
  else if (d.id && (d.subject || d.snippet || d.text) && 
           (d.subject?.includes('Jiffy') || d.from?.includes('jiffy'))) {
    source = 'jiffy';

    // Basic shipment data from raw email
    shipmentData = {
      orderId: `JIFFY-${Date.now()}`,
      emailId: d.id,
      subject: d.subject,
      requiresProcessing: true
    };
  }

  // Additional source detection patterns
  else if (d.source) {
    // If source is already defined, use it
    source = d.source;
    shipmentData = {
      orderId: d.orderId || d.id,
      ...d // Include all existing data
    };
  }

  // Build the final output structure
  return {
    json: {
      // Root level source
      source: source,

      // Shipment-ready data
      shipment: {
        source: source,
        sourceData: shipmentData,

        // Common fields across all sources
        orderId: shipmentData.orderId,
        customer: shipmentData.customer || {},
        shippingAddress: shipmentData.shippingAddress || {},
        items: shipmentData.items || [],
        financial: shipmentData.financial || {},

        // Shipping specific
        shippingLabel: shipmentData.shippingLabel,
        shipping_label_url: shipmentData.shipping_label_url,
        rushService: shipmentData.rushService,

        // File information - ADDED cutlineReferences
        files: shipmentData.files,
        gangSheetUrls: shipmentData.gangSheetUrls,
        cutlineReferences: shipmentData.cutlineReferences,

        // Meta
        createdAt: new Date().toISOString(),
        requiresProcessing: shipmentData.requiresProcessing || false
      },

      // Original payload for reference
      payload: d,

      // Additional metadata
      meta: {
        processedAt: new Date().toISOString(),
        hasShippingLabel: !!shipmentData.shipping_label_url,
        hasCutlineReferences: !!(shipmentData.cutlineReferences && shipmentData.cutlineReferences.length > 0),
        isReadyForShipment: !!(shipmentData.orderId && shipmentData.shippingAddress)
      }
    },

    // Preserve binary data if exists
    binary: item.binary
  };
});

// Helper function to format addresses with proper title case
function formatAddress(address) {
  if (!address) return '';

  // Replace line breaks with comma and space, clean up extra spaces
  const cleaned = address
    .replace(/\r?\n/g, ', ')  // Replace line breaks with comma
    .replace(/,\s*,/g, ',')   // Remove duplicate commas
    .replace(/\s+/g, ' ')     // Replace multiple spaces with single space
    .trim();

  const states = ['AL','AK','AZ','AR','CA','CO','CT','DE','FL','GA','HI','ID','IL','IN','IA','KS','KY','LA','ME','MD','MA','MI','MN','MS','MO','MT','NE','NV','NH','NJ','NM','NY','NC','ND','OH','OK','OR','PA','RI','SC','SD','TN','TX','UT','VT','VA','WA','WV','WI','WY','DC'];

  return cleaned.split(/\b/).map(part => {
    const word = part.trim();
    if (!word || /^[,\s]+$/.test(part)) return part;

    // Keep state codes uppercase
    if (states.includes(word.toUpperCase())) {
      return word.toUpperCase();
    }

    // Keep ZIP codes as-is
    if (/^\d{5}(-\d{4})?$/.test(word)) {
      return word;
    }

    // Title case other words
    if (/^[a-zA-Z]+$/.test(word)) {
      return word.charAt(0).toUpperCase() + word.slice(1).toLowerCase();
    }

    return word;
  }).join('');
}

// Helper function to create formatted full address
function createFullAddress(addressObj) {
  if (!addressObj) return '';

  const parts = [
    addressObj.address1,
    addressObj.address2,
    addressObj.city,
    addressObj.province_code || addressObj.state,
    addressObj.zip
  ].filter(Boolean);

  return formatAddress(parts.join(', '));
}

// Helper function to extract filename from URL
function extractFilenameFromUrl(url) {
  if (!url) return '';

  try {
    // Extract the last part of the URL path
    const urlParts = url.split('/');
    const filename = urlParts[urlParts.length - 1];

    // Remove query parameters if present
    return filename.split('?')[0] || '';
  } catch (e) {
    return '';
  }
}

// Helper function to determine if title is a default product name
function isDefaultProductTitle(title) {
  if (!title) return false;

  const defaultTitles = [
    'DTF Transfer Online Gang Sheet Builder PRO For Clothing',
    'DTF Transfer Online Gang Sheet Builder PRO',
    'Gang Sheet Builder',
    'DTF Online Builder',
    'Online Gang Sheet Builder',
    'UV Sticker Online Gang Sheet Builder',
    'UV Online Builder',
    'Sublimation Transfer Builder',
    'Heat Press Builder',
    'Custom Transfer Builder',
    'Online Transfer Builder',
    'Transfer Builder PRO',
    'Custom Gang Sheet Builder'
  ];

  return defaultTitles.some(defaultTitle => 
    title.toLowerCase().includes(defaultTitle.toLowerCase())
  );
}

// Enhanced product type detection based on Airtable schema
function detectProductType(lineItem) {
  const title = (lineItem.title || '').toLowerCase();
  const sku = (lineItem.sku || '').toLowerCase();
  const variantTitle = (lineItem.variant_title || '').toLowerCase();
  const properties = lineItem.properties || [];

  // Check if it's a sample pack first
  if (isSamplePackItem(lineItem)) {
    return 'Sample Pack';
  }

  // Check properties for product type
  const productTypeProp = properties.find(p => 
    p.name && p.name.toLowerCase() === 'product type'
  );
  if (productTypeProp && productTypeProp.value) {
    // Map to exact Airtable values
    const typeMap = {
      'dtf': 'DTF Transfers',
      'dtf gang sheet': 'DTF Gang Sheet',
      'uv gang sheet': 'UV Gang Sheet',
      'gang sheet': 'Gang Sheet', // Generic gang sheet
      'uv': 'UV Stickers',
      'sublimation': 'Sublimation',
      'heat press': 'Heat Press',
      'heat tape': 'Heat Tape',
      'matt finishing': 'Matt Finishing Sheet',
      'matt finishing sheet': 'Matt Finishing Sheet',
      'laser alignment': 'Laser Alignment',
      'alignment tool': 'Alignment Tool',
      'accessories': 'Accessories',
      'dtf + uv': 'DTF + UV DTF',
      'sample': 'Sample Pack',
      'sample pack': 'Sample Pack'
    };

    const propValue = productTypeProp.value.toLowerCase();
    for (const [key, value] of Object.entries(typeMap)) {
      if (propValue.includes(key)) return value;
    }
  }

  // Special handling for Gang Sheet Builder products
  if (title.includes('gang sheet builder')) {
    // Check if it's UV or DTF gang sheet
    if (title.includes('uv')) {
      return 'UV Gang Sheet';
    } else {
      // Default gang sheet builder products to DTF
      return 'DTF Gang Sheet';
    }
  }

  // Check for specific product types in title/SKU
  if (title.includes('laser alignment') || sku.includes('laser-align')) return 'Laser Alignment';
  if (title.includes('alignment tool') || sku.includes('align-tool')) return 'Alignment Tool';
  if (title.includes('matt finishing') || title.includes('matte finishing')) return 'Matt Finishing Sheet';
  if (title.includes('heat tape') || sku.includes('heat-tape')) return 'Heat Tape';
  if (title.includes('uv') && title.includes('dtf')) return 'DTF + UV DTF';
  if (title.includes('uv') && title.includes('gang')) return 'UV Gang Sheet';
  if (title.includes('dtf') && title.includes('gang')) return 'DTF Gang Sheet';
  if (title.includes('uv') || sku.includes('uv')) return 'UV Stickers';
  if (title.includes('sublimation') || sku.includes('sublimation')) return 'Sublimation';
  if (title.includes('heat press') || title.includes('heat-press')) return 'Heat Press';
  if (title.includes('gang sheet') || sku.includes('gang')) {
    // Generic gang sheet - check other clues
    if (variantTitle.includes('uv')) return 'UV Gang Sheet';
    return 'DTF Gang Sheet'; // Default to DTF
  }
  if (title.includes('accessory') || title.includes('accessories')) return 'Accessories';

  // Check variant title
  if (variantTitle.includes('uv')) return 'UV Stickers';
  if (variantTitle.includes('gang')) {
    if (variantTitle.includes('uv')) return 'UV Gang Sheet';
    return 'DTF Gang Sheet';
  }

  // Default to DTF Transfers
  return 'DTF Transfers';
}

// Determine facility based on order data
function determineFacility(order, lineItems) {
  // For Shopify orders, leave facility blank for user selection
  return null;
}

// Helper function to determine if order needs files
function orderNeedsFiles(lineItems) {
  if (!Array.isArray(lineItems)) return false;

  return lineItems.some(li => {
    // Sample packs don't need files
    if (isSamplePackItem(li)) return false;

    const properties = li.properties || [];
    const hasOriginal = properties.find(p => p.name === '_original_image');
    const hasDpi300 = properties.find(p => p.name === '_dpi300_image');
    const hasPrintReady = properties.find(p => p.name === '_Print Ready File');
    return !hasOriginal && !hasDpi300 && !hasPrintReady;
  });
}

// Helper function to check if item is a sample pack
function isSamplePackItem(lineItem) {
  const title = (lineItem.title || '').toLowerCase();
  const sku = (lineItem.sku || '').toLowerCase();
  const variantTitle = (lineItem.variant_title || '').toLowerCase();

  return title.includes('sample') || 
         title.includes('sample pack') || 
         title.includes('swatch') ||
         sku.includes('sample') ||
         sku.includes('sample-pack') ||
         variantTitle.includes('sample');
}

// Helper function to check if order is sample pack only
function isSamplePackOnlyOrder(lineItems, shippingAmount) {
  if (!Array.isArray(lineItems) || lineItems.length === 0) return false;

  // Check if ALL items are sample packs
  const allItemsAreSamples = lineItems.every(item => isSamplePackItem(item));

  // Check if shipping is $2
  const hasLowShipping = shippingAmount === 2 || shippingAmount === 2.00;

  return allItemsAreSamples && hasLowShipping;
}

// Process ALL items
const items = $input.all();
const allOutputs = [];

items.forEach(item => {
  try {
    const order = item.json.payload;

    // Validate order structure
    if (!order || typeof order !== 'object') {
      throw new Error('Invalid order structure');
    }

    // UPDATED: Format IDs according to new requirements
    // Extract the TSS number from order.name (e.g., "#TSS9183" -> "TSS9183")
    const tssNumber = (order.name || '').replace('#', '').trim();

    // orderId: 'TS-TSS9183' format
    const orderId = tssNumber ? `TS-${tssNumber}` : `TS-TSS${order.order_number || Date.now()}`;

    // submissionId: 'TSS9183' (without # or TS- prefix)
    const submissionId = tssNumber || `TSS${order.order_number || Date.now()}`;

    // shopifyOrderNumber: the numeric Shopify ID
    const shopifyOrderNumber = order.id || '';

    // Extract order details with validation
    const hasRushTag = (order.tags || '').toLowerCase().includes('rush');
    const has24HourTag = (order.tags || '').toLowerCase().includes('24 hour') || 
                         (order.tags || '').toLowerCase().includes('24hr');
    const hasSuperRushTag = (order.tags || '').toLowerCase().includes('super rush');

    // Determine rush service level for Production Option
    let productionOption = 'Standard 2-3 Days'; // Default
    let rushService = false; // Checkbox - boolean

    if (hasSuperRushTag || has24HourTag) {
      productionOption = 'Super Rush 24 hrs';
      rushService = true;
    } else if (hasRushTag) {
      productionOption = 'Rush 1-2 Days';
      rushService = true;
    }

    const precut = (order.tags || '').toLowerCase().includes('precut') || 
                   (order.tags || '').toLowerCase().includes('pre-cut');

    // Check for gang sheet required - includes "by Size" products
    const gangSheetRequired = (order.line_items || []).some(li => 
      (li.title || '').toLowerCase().includes('gang sheet') ||
      (li.title || '').toLowerCase().includes('by size') ||
      (li.properties || []).some(p => p.name === 'Gang Sheet' && p.value)
    );

    const shippingLine = (order.shipping_lines && order.shipping_lines[0]) || {};
    const shippingAmount = parseFloat(order.total_shipping_price_set?.shop_money?.amount || 0);
    const needsFile = orderNeedsFiles(order.line_items || []);

    // Determine facility for this order
    const facility = determineFacility(order, order.line_items || []);

    // Extract all product types
    const productTypes = [...new Set((order.line_items || []).map(li => detectProductType(li)))];

    // Extract discount information
    const discountCodes = order.discount_codes || [];
    const discountAmount = parseFloat(order.total_discounts || 0);

    // Check if this is a sample pack only order
    const isSamplePackOnly = isSamplePackOnlyOrder(order.line_items || [], shippingAmount);

    // Determine fulfillment and shipping options
    let fulfillmentOption = 'Ship'; // Default
    let shippingOption = 'Ground'; // Default

    const shippingTitle = (shippingLine.title || '').toLowerCase();
    const shippingCode = (shippingLine.code || '').toLowerCase();
    const shippingText = shippingTitle + ' ' + shippingCode;

    // First check if it's Will Call (fulfillment option)
    if (shippingText.includes('will call') || 
        shippingText.includes('pickup') || 
        shippingText.includes('pick up') ||
        shippingText.includes('local') ||
        shippingText.includes('transfer superstars hq') ||
        shippingAmount === 0) {
      fulfillmentOption = 'Will Call';
      shippingOption = null; // No shipping option needed for Will Call
    } else {
      // It's a Ship order, determine shipping speed
      if (shippingText.includes('overnight') || 
          shippingText.includes('next day') || 
          shippingText.includes('1 day') ||
          shippingText.includes('1-day') ||
          shippingText.includes('priority overnight')) {
        shippingOption = 'Overnight';
      } else if (shippingText.includes('express') || 
                 shippingText.includes('2 day') || 
                 shippingText.includes('2-day') ||
                 shippingText.includes('second day')) {
        shippingOption = 'Express';
      } else {
        shippingOption = 'Ground';
      }
    }

    // Extract order tags - lowercase, meaningful tags
    const orderTags = [];

    // Add tags from order.tags
    if (order.tags) {
      const rawTags = order.tags.split(',').map(t => t.trim().toLowerCase()).filter(Boolean);

      // Filter and add meaningful tags
      rawTags.forEach(tag => {
        if (tag.includes('rush')) orderTags.push('rush');
        if (tag.includes('precut') || tag.includes('pre-cut')) orderTags.push('precut');
        if (tag.includes('sample')) orderTags.push('sample');
        if (tag.includes('wholesale')) orderTags.push('wholesale');
        if (tag.includes('repeat')) orderTags.push('repeat-customer');
        if (tag.includes('vip')) orderTags.push('vip');
        if (tag.includes('priority')) orderTags.push('priority');
      });
    }

    // Add automatic tags based on order properties
    if (rushService && !orderTags.includes('rush')) orderTags.push('rush');
    if (precut && !orderTags.includes('precut')) orderTags.push('precut');
    if (gangSheetRequired && !orderTags.includes('gang-sheet')) orderTags.push('gang-sheet');
    if (isSamplePackOnly && !orderTags.includes('sample')) orderTags.push('sample');

    // Add customer-based tags
    if (order.customer?.tags?.includes('wholesale') && !orderTags.includes('wholesale')) {
      orderTags.push('wholesale');
    }
    if (order.customer?.orders_count > 1 && !orderTags.includes('repeat-customer')) {
      orderTags.push('repeat-customer');
    }
    if (order.total_price > 500 && !orderTags.includes('high-value')) {
      orderTags.push('high-value');
    }

    // Add payment tags
    if (order.financial_status === 'paid' && !orderTags.includes('paid')) {
      orderTags.push('paid');
    }

    // Remove duplicates
    const uniqueTags = [...new Set(orderTags)];

    // Determine priority level based on production option
    let priorityLevel = 'Normal';
    if (rushService) priorityLevel = 'High';

    // Create order summary
    const orderSummary = `Order: ${orderId}
Date: ${new Date(order.created_at).toLocaleDateString()}
Customer: ${order.customer?.first_name} ${order.customer?.last_name} (${order.customer?.email || order.email})
${order.billing_address?.company ? `Company: ${order.billing_address.company}\n` : ''}
Items: ${(order.line_items || []).length} item(s)
${productTypes.length > 0 ? `Products: ${productTypes.join(', ')}\n` : ''}
Total: ${order.total_price} ${order.currency}
${rushService ? `âš¡ ${productionOption}\n` : ''}${precut ? 'âœ‚ï¸ Pre-cut Required\n' : ''}${gangSheetRequired ? 'ðŸ“ Gang Sheet Required\n' : ''}
Fulfillment: ${fulfillmentOption}${fulfillmentOption === 'Ship' ? ` - ${shippingOption}` : ''}
${order.note ? `\nCustomer Notes: ${order.note}` : ''}
${discountCodes.length > 0 ? `\nDiscounts: ${discountCodes.map(d => d.code).join(', ')} (-${discountAmount})` : ''}
Status: ${order.financial_status === 'paid' ? 'âœ… Paid' : 'â³ Payment Pending'}`;

    // Determine file status
    let fileStatus = 'All Files Received';
    if (needsFile) fileStatus = 'Awaiting Files';
    else if ((order.line_items || []).some(li => {
      const props = li.properties || [];
      return props.some(p => p.name === '_dpi300_image' && !p.value);
    })) fileStatus = 'Processing';

    const orderCanon = {
      // UPDATED: Identification with new format
      orderId,           // 'TS-TSS9183'
      submissionId,      // 'TSS9183'
      source: 'Shopify',
      shopifyOrderNumber, // numeric ID like 6329978978592

      // Timestamps
      orderDate: order.created_at || new Date().toISOString(),
      createdAt: order.created_at || new Date().toISOString(),
      updatedAt: order.updated_at || new Date().toISOString(),
      paidAt: order.processed_at || null,
      fulfilledAt: order.closed_at || null,

      // Customer data - enhanced
      customer: {
        id: (order.customer?.id || '').toString(),
        email: order.customer?.email || order.email || order.contact_email || '',
        name: order.customer?.first_name && order.customer?.last_name ? 
              `${order.customer.first_name} ${order.customer.last_name}` :
              order.billing_address?.name || order.shipping_address?.name || 'Unknown',
        firstName: order.customer?.first_name || order.billing_address?.first_name || order.shipping_address?.first_name || '',
        lastName: order.customer?.last_name || order.billing_address?.last_name || order.shipping_address?.last_name || '',
        phone: order.customer?.phone || order.billing_address?.phone || order.shipping_address?.phone || order.phone || '',
        company: order.billing_address?.company || order.shipping_address?.company || order.customer?.default_address?.company || '',
        tags: order.customer?.tags || '',
        note: order.customer?.note || '',
        emailOptIn: order.buyer_accepts_marketing || false,
        smsOptIn: order.customer?.sms_marketing_consent?.state === 'subscribed' || false,
        taxExempt: order.customer?.tax_exempt || false,
        verifiedEmail: order.customer?.verified_email || false,
        ordersCount: order.customer?.orders_count || 1,
        totalSpent: order.customer?.total_spent || order.total_price || 0
      },

      // Shipping address - complete with formatting
      shipping: {
        fullAddress: createFullAddress(order.shipping_address),
        name: order.shipping_address?.name || '',
        firstName: order.shipping_address?.first_name || '',
        lastName: order.shipping_address?.last_name || '',
        company: order.shipping_address?.company || '',
        address1: order.shipping_address?.address1 || '',
        address2: order.shipping_address?.address2 || '',
        city: order.shipping_address?.city || '',
        state: order.shipping_address?.province_code || '',
        zip: order.shipping_address?.zip || '',
        country: order.shipping_address?.country || '',
        countryCode: order.shipping_address?.country_code || '',
        phone: order.shipping_address?.phone || '',
        method: shippingOption  // DEPRECATED - use fulfillmentOption and shippingOption instead
      },

      // Billing address - complete with formatting
      billing: {
        fullAddress: createFullAddress(order.billing_address),
        name: order.billing_address?.name || '',
        firstName: order.billing_address?.first_name || '',
        lastName: order.billing_address?.last_name || '',
        company: order.billing_address?.company || '',
        address1: order.billing_address?.address1 || '',
        address2: order.billing_address?.address2 || '',
        city: order.billing_address?.city || '',
        state: order.billing_address?.province_code || '',
        zip: order.billing_address?.zip || '',
        country: order.billing_address?.country || '',
        countryCode: order.billing_address?.country_code || '',
        phone: order.billing_address?.phone || ''
      },

      // Financial data - enhanced
      financial: {
        subtotal: parseFloat(order.subtotal_price || 0),
        shipping: shippingAmount,
        tax: parseFloat(order.total_tax || 0),
        total: parseFloat(order.total_price || 0),
        discountAmount: discountAmount,
        discountCodes: discountCodes.map(d => d.code).join(', '),
        currency: order.currency || 'USD',
        paymentStatus: order.financial_status === 'paid' ? 'Paid' : 'Pending Payment',
        paymentMethod: (order.payment_gateway_names && order.payment_gateway_names[0]) || 'Unknown',
        refundedAmount: parseFloat(order.total_refunded || 0),
        outstandingBalance: parseFloat(order.total_outstanding || 0)
      },

      // Order status and options
      status: {
        orderStatus: 'New',
        fulfillmentStatus: order.fulfillment_status || null,
        syncStatus: 'Success',
        qcStatus: 'Not Started'
      },

      // Production options with facility
      options: {
        rushService,  // Boolean checkbox
        productionOption,  // "Standard 2-3 Days", "Rush 1-2 Days", "Super Rush 24 hrs"
        fulfillmentOption,  // "Ship" or "Will Call"
        shippingOption,  // "Ground", "Express", "Overnight" (null for Will Call)
        precut,
        gangSheetRequired,
        needsFile,
        notes: order.note || '',
        customerNotes: order.note || '',
        internalNotes: '',
        tags: uniqueTags.join(','), // lowercase, comma-separated
        priorityLevel,
        productTypes: productTypes.join(', '),
        fileStatus,
        isSamplePackOnly,
        facility,
        orderSummary
      },

      // Summary fields
      summary: {
        itemCount: (order.line_items || []).length,
        sourceUrl: `https://admin.shopify.com/store/transfer-superstars/orders/${order.id || ''}`
      },

      // Complete metadata
      meta: {
        shopify: order,
        facility,
        processedAt: new Date().toISOString()
      }
    };

    // Create array of line items with complete mapping
    const lineItems = (order.line_items || []).map(li => {
      // Extract all properties
      const properties = li.properties || [];
      const propertiesObj = {};
      properties.forEach(p => {
        if (p.name) propertiesObj[p.name] = p.value;
      });

      // File handling
      const originalImage = properties.find(p => p.name === '_original_image');
      const dpi300Image = properties.find(p => p.name === '_dpi300_image');
      const previewImage = properties.find(p => p.name === 'Preview');
      const printReadyFile = properties.find(p => p.name === '_Print Ready File');

      // Extract dimensions with multiple fallbacks
      const sizeProp = properties.find(p => /size/i.test(p.name||''))?.value || '';
      const dimensionsProp = properties.find(p => p.name === 'dimensions')?.value || '';
      const dims = sizeProp || dimensionsProp || 
                   (li.variant_title || '').replace(/[^\d.x]/g, '') ||
                   ((li.sku || '').match(/(\d+\.?\d*)x(\d+\.?\d*)/)||[]).join('x');

      // File quality info
      const dpi = properties.find(p => p.name === 'dpi')?.value || '';
      const colorMode = properties.find(p => p.name === 'color_mode')?.value || '';
      const fileFormat = properties.find(p => p.name === 'file_format')?.value || '';

      // Determine file URL (prioritize DPI300, then print ready, then original)
      const fileUrl = dpi300Image?.value || printReadyFile?.value || 
                     originalImage?.value || previewImage?.value || '';

      // Get filename - extract from URL if title is generic
      let fileName = properties.find(p => p.name === 'File name')?.value || '';

      // If no filename or title is a default product name, extract from URL
      if (!fileName || isDefaultProductTitle(li.title)) {
        if (fileUrl) {
          fileName = extractFilenameFromUrl(fileUrl);
        }
      }

      // Production options
      const isPrecut = properties.find(p => p.name === 'Precut')?.value === 'Yes' ||
                      properties.find(p => p.name === 'Pre-cut')?.value === 'Yes' ||
                      precut; // Inherit from order level
      const addWeeding = properties.find(p => p.name === 'Add Weeding')?.value === 'Yes';
      const isGangSheet = properties.find(p => p.name === 'Gang Sheet')?.value === 'Yes' ||
                         detectProductType(li) === 'Gang Sheet' ||
                         detectProductType(li) === 'DTF Gang Sheet' ||
                         detectProductType(li) === 'UV Gang Sheet';
      const transferTape = properties.find(p => p.name === 'Transfer Tape')?.value === 'Yes';
      const gangSheetLength = parseFloat(properties.find(p => p.name === 'Gang Sheet Length')?.value || 0);

      // Check if this is a sample pack
      const isSamplePack = isSamplePackItem(li);

      // Determine item status
      let itemStatus = 'Pending';

      // Sample packs are always ready - they don't need files
      if (isSamplePack) {
        itemStatus = 'Ready';
      } else if (fileUrl && dpi300Image?.value) {
        itemStatus = 'Ready';
      } else if (fileUrl) {
        itemStatus = 'Processing';
      } else {
        itemStatus = 'Pending';
      }

      // Determine artwork QC status
      let artworkQC = 'Pending';
      if (properties.find(p => p.name === 'QC Approved')?.value === 'Yes') {
        artworkQC = 'Approved';
      } else if (properties.find(p => p.name === 'QC Rejected')?.value === 'Yes') {
        artworkQC = 'Rejected';
      }

      return {
        // UPDATED: Identification with new orderId format
        itemUid: `${orderId}-${li.id || Date.now()}`,  // 'TS-TSS9183-123456'
        orderId,  // 'TS-TSS9183'
        lineItemId: (li.id || Date.now()).toString(),

        // Product info with proper type detection
        productType: detectProductType(li),
        productId: (li.product_id || '').toString(),
        variantId: (li.variant_id || '').toString(),
        sku: li.sku || '',
        title: li.title || '',
        variantTitle: li.variant_title || '',
        vendor: li.vendor || '',

        // Quantities and pricing
        qty: Number(li.quantity || 1),
        unitPrice: Number(li.price || 0),
        compareAtPrice: Number(li.compare_at_price || 0),
        totalDiscount: Number(li.total_discount || 0),

        // Physical properties
        dimensions: dims,
        weight: li.grams || 0,
        requiresShipping: li.requires_shipping !== false,

        // File information - Sample packs don't need files
        fileUrl: isSamplePack ? 'N/A - Sample Pack' : fileUrl,
        needsFile: isSamplePack ? false : !fileUrl,
        fileName: isSamplePack ? 'Sample Pack - No File Required' : fileName,
        originalFileUrl: isSamplePack ? '' : (originalImage?.value || ''),
        processedFileUrl: isSamplePack ? '' : (dpi300Image?.value || ''),
        previewUrl: isSamplePack ? '' : (previewImage?.value || ''),

        // File quality
        fileDpi: Number(dpi) || 300,
        fileColorMode: colorMode || 'RGB',
        fileFormat: fileFormat || (fileName ? fileName.split('.').pop()?.toUpperCase() : 'PNG') || 'PNG',
        fileDimensions: dimensionsProp,

        // Production options
        isPrecut,
        addWeeding,
        isGangSheet,
        transferTape,
        gangSheetLength,
        isSamplePack,

        // Fulfillment
        fulfillableQuantity: li.fulfillable_quantity || li.quantity || 0,
        fulfillmentService: li.fulfillment_service || 'manual',
        fulfillmentStatus: li.fulfillment_status || null,

        // Tax info
        taxable: li.taxable !== false,

        // Gift card
        giftCard: li.gift_card || false,

        // Status
        itemStatus,
        artworkQC,
        qcNotes: properties.find(p => p.name === 'QC Notes')?.value || '',

        // Facility assignment
        facility: facility,

        // Complete metadata
        meta: {
          shopify: {
            lineItemId: li.id,
            properties: properties,
            propertiesObject: propertiesObj,
            productHandle: li.product_handle || ''
          },
          facility: facility,
          processedAt: new Date().toISOString()
        }
      };
    });

    // Add this order with its items to output
    allOutputs.push({
      json: {
        order: orderCanon,
        items: lineItems
      }
    });
  } catch (error) {
    // Log error but continue processing other orders
    console.error('Error processing order:', error.message);
  }
});

return allOutputs.length > 0 ? allOutputs : [{ json: { error: 'No valid orders to process' } }];

// Helper function to extract filename from URL
function extractFilenameFromUrl(url) {
  if (!url) return '';

  try {
    // Decode the URL first to handle encoded characters
    const decodedUrl = decodeURIComponent(url);
    // Extract the last part of the URL path
    const urlParts = decodedUrl.split('/');
    const filename = urlParts[urlParts.length - 1];

    // Remove query parameters if present
    return filename.split('?')[0] || '';
  } catch (e) {
    // If decoding fails, try without decoding
    const urlParts = url.split('/');
    const filename = urlParts[urlParts.length - 1];
    return filename.split('?')[0] || '';
  }
}

// Enhanced product type detection based on Airtable schema and form data
function detectProductType(productType, formName, answers, formId) {
  const type = (productType || '').toLowerCase();
  const form = (formName || '').toLowerCase();

  // First check direct product type answer
  if (productType) {
    const typeMap = {
      'dtf': 'DTF Transfers',
      'dtf transfer': 'DTF Transfers',
      'dtf transfers': 'DTF Transfers',
      'dtf gang sheet': 'DTF Gang Sheet',
      'uv gang sheet': 'UV Gang Sheet',
      'gang sheet': 'Gang Sheet',
      'gang sheets': 'Gang Sheet',
      'uv': 'UV Stickers',
      'uv sticker': 'UV Stickers',
      'uv stickers': 'UV Stickers',
      'sublimation': 'Sublimation',
      'heat press': 'Heat Press',
      'heat tape': 'Heat Tape',
      'matt finishing': 'Matt Finishing Sheet',
      'matt finishing sheet': 'Matt Finishing Sheet',
      'laser alignment': 'Laser Alignment',
      'alignment tool': 'Alignment Tool',
      'accessories': 'Accessories',
      'dtf + uv': 'DTF + UV DTF',
      'dtf and uv': 'DTF + UV DTF',
      'uv dtf': 'DTF + UV DTF'
    };

    for (const [key, value] of Object.entries(typeMap)) {
      if (type === key || type.includes(key)) {
        // Special handling for generic "Gang Sheet"
        if (value === 'Gang Sheet') {
          // Determine based on form
          if (form.includes('uv') || formId === '233115151476147') {
            return 'UV Gang Sheet';
          } else {
            return 'DTF Gang Sheet';
          }
        }
        return value;
      }
    }
  }

  // Check form name for product type hints
  if (form.includes('uv') && form.includes('dtf')) return 'DTF + UV DTF';
  if (form.includes('uv') && form.includes('gang')) return 'UV Gang Sheet';
  if (form.includes('dtf') && form.includes('gang')) return 'DTF Gang Sheet';
  if (form.includes('uv sticker') || form.includes('uv-sticker')) return 'UV Stickers';
  if (form.includes('uv')) return 'UV Stickers';
  if (form.includes('sublimation')) return 'Sublimation';
  if (form.includes('heat press')) return 'Heat Press';
  if (form.includes('gang sheet') || form.includes('gangsheet')) {
    // Try to determine if UV or DTF
    if (form.includes('uv') || formId === '233115151476147') {
      return 'UV Gang Sheet';
    }
    return 'DTF Gang Sheet'; // Default to DTF
  }

  // Check special instructions or notes for product type hints
  if (answers) {
    for (const answer of Object.values(answers)) {
      const text = (answer.answer || answer.prettyFormat || '').toString().toLowerCase();
      if (text.includes('uv') && text.includes('dtf')) return 'DTF + UV DTF';
      if (text.includes('uv') && text.includes('gang')) return 'UV Gang Sheet';
      if (text.includes('dtf') && text.includes('gang')) return 'DTF Gang Sheet';
      if (text.includes('uv sticker')) return 'UV Stickers';
      if (text.includes('gang sheet')) {
        if (text.includes('uv') || formId === '233115151476147') {
          return 'UV Gang Sheet';
        }
        return 'DTF Gang Sheet';
      }
      if (text.includes('sublimation')) return 'Sublimation';
      if (text.includes('alignment tool')) return 'Alignment Tool';
    }
  }

  // Default to DTF Transfers
  return 'DTF Transfers';
}

