// Extract order tags based on form data
function extractOrderTags(rushService, precut, formName, answers) {
  const tags = [];

  if (rushService) tags.push('rush');
  if (precut) tags.push('precut');

  // Add form-specific tags
  const form = (formName || '').toLowerCase();
  if (form.includes('wholesale') || form.includes('b2b')) {
    tags.push('wholesale');
  }

  if (form.includes('sample')) {
    tags.push('sample');
  }

  // Check answers for additional tags
  if (answers) {
    for (const [key, answer] of Object.entries(answers)) {
      const text = (answer.text || '').toLowerCase();
      const value = (answer.answer || answer.prettyFormat || '').toString().toLowerCase();

      if (text.includes('repeat customer') && value.includes('yes')) {
        tags.push('repeat-customer');
      }

      if (text.includes('referral') && value) {
        tags.push('referral');
      }
    }
  }

  return tags;
}

// Process ALL items
const items = $input.all();

return items.map(item => {
  try {
    const submission = item.json.payload || item.json;
    const answers = submission.answers || {};

    // Validate submission
    if (!submission.id) {
      throw new Error('Invalid JotForm submission - no ID');
    }

    // Get form metadata from the tagged data
    const formName = item.json.form_name || '';
