    // UPDATED: Format IDs according to new requirements
    // JotForm: orderId = "DTF-2398234792374" or "UV-234792873472", submissionId = "2398234792374"
    const submissionId = submission.id.toString();
    const orderId = `${prefix}-${submissionId}`;

    let customer = {
      email: '',
      name: '',
      firstName: '',
      lastName: '',
      phone: '',
      company: '',
      emailOptIn: true,
      smsOptIn: false,
      taxExempt: false,
      verifiedEmail: false,
      tags: '',
      note: ''
    };

    let shipping = {
      fullAddress: '',
      name: '',
      firstName: '',
      lastName: '',
      company: '',
      address1: '',
      address2: '',
      city: '',
      state: '',
      zip: '',
      country: 'US',
      countryCode: 'US',
      phone: '',
      method: 'Ground' // Default to Ground
    };

    let billing = {
      fullAddress: '',
      name: '',
      firstName: '',
      lastName: '',
      company: '',
      address1: '',
      address2: '',
      city: '',
      state: '',
      zip: '',
      country: 'US',
      countryCode: 'US',
      phone: ''
    };

    let orderDetails = {
      productType: '',
      quantity: 1,
      dimensions: '',
      fileData: [], // Changed to store file objects with url and name
      specialInstructions: '',
      productionOption: 'Standard 2-3 Days', // Default production option
      rushService: false, // Boolean checkbox
      precut: false,
      gangSheetRequired: false,
      addWeeding: false,
      transferTape: false,
      paymentMethod: 'Pending',
      referralSource: '',
      fulfillmentOption: 'Ship', // Default fulfillment option
      shippingOption: 'Ground' // Default shipping option
    };

    // Debug log to see what we're getting
    console.log(`Processing submission ${submissionId} with ${Object.keys(answers).length} answers`);

    // Parse JotForm answers - use for...of instead of forEach
    for (const [key, answer] of Object.entries(answers)) {
      if (!answer || typeof answer !== 'object') continue;

      const text = answer.text || '';
      const value = answer.answer || answer.prettyFormat || '';

      // Debug file upload fields
      if (answer.type === 'control_fileupload' || text.includes('Upload') || text.includes('File')) {
        console.log(`Found file upload field ${key}:`, answer);
      }

      // Customer info - improved detection with specific field IDs
      // Email - check specific field ID 331 first
      if (key === '331') {
        // This is the email field
        if (typeof answer.answer === 'string') {
          customer.email = answer.answer;
        } else if (typeof answer.prettyFormat === 'string') {
          customer.email = answer.prettyFormat;
        } else if (typeof value === 'string') {
          customer.email = value;
        }
        console.log(`Found email in field 331: ${customer.email}`);
      } else if (answer.type === 'control_email' || 
                 text.toLowerCase().includes('email') || 
                 text.toLowerCase().includes('e-mail')) {
        // Fallback email detection
        if (typeof value === 'string' && value.includes('@')) {
          customer.email = value;
        } else if (typeof answer.answer === 'string' && answer.answer.includes('@')) {
          customer.email = answer.answer;
        } else if (answer.prettyFormat && typeof answer.prettyFormat === 'string') {
          customer.email = answer.prettyFormat;
        }
      } else if (answer.type === 'control_fullname' || 
                 (text.toLowerCase().includes('name') && 
                  !text.toLowerCase().includes('company') && 
                  !text.toLowerCase().includes('business'))) {
        // Handle fullname field - JotForm often returns an object
        if (typeof answer.answer === 'object' && answer.answer) {
          customer.firstName = answer.answer.first || '';
          customer.lastName = answer.answer.last || '';
          customer.name = `${customer.firstName} ${customer.lastName}`.trim();
        } else if (typeof value === 'string' && value) {
          customer.name = value;
          const nameParts = value.split(' ');
          customer.firstName = nameParts[0] || '';
          customer.lastName = nameParts.slice(1).join(' ') || '';
        }
      } else if (answer.type === 'control_phone' || text.toLowerCase().includes('phone')) {
        customer.phone = answer.answer?.full || (typeof value === 'string' ? value : '');
      } else if ((text.toLowerCase().includes('company') || text.toLowerCase().includes('business')) && 
                 !text.toLowerCase().includes('address')) {
        // Only capture as company if it's actually a company field, not a date or other field
        const companyValue = typeof value === 'string' ? value : '';
        // Check if the value looks like a date (e.g., "Wed Jun 25")
        if (!companyValue.match(/^(Mon|Tue|Wed|Thu|Fri|Sat|Sun)\s+(Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)\s+\d+/i)) {
          customer.company = companyValue;
        }
      }

      // Address info
      if ((answer.type === 'control_address' || text.toLowerCase().includes('address')) && answer.answer && typeof answer.answer === 'object') {
        const addr = answer.answer;
        shipping.address1 = addr.addr_line1 || '';
        shipping.address2 = addr.addr_line2 || '';
        shipping.city = addr.city || '';
        shipping.state = addr.state || '';
        shipping.zip = addr.postal || '';
        shipping.country = addr.country || 'United States';
        shipping.countryCode = addr.country === 'United States' ? 'US' : addr.country?.substring(0, 2).toUpperCase() || 'US';

        // Format full address
        const parts = [shipping.address1, shipping.address2, shipping.city, shipping.state, shipping.zip].filter(Boolean);
        shipping.fullAddress = parts.join(', ');

        // Copy to billing
        billing = {...shipping};
      }

      // Product details - Enhanced detection
      if (text.includes('Product Type') || text.includes('What are you printing') || text.includes('Service Type')) {
        // Handle multiple choice or dropdown answers
        if (typeof value === 'string') {
          orderDetails.productType = value;
        } else if (Array.isArray(value) && value.length > 0) {
          orderDetails.productType = value[0];
        }
      } else if (text.includes('Quantity') || text.includes('How many')) {
        // Handle matrix field for quantity
        if (answer.answer && Array.isArray(answer.answer)) {
          // Matrix field structure: [[row_name, col1_value, col2_value, ...]]
          if (Array.isArray(answer.answer[0])) {
            orderDetails.quantity = parseInt(answer.answer[0][2]) || parseInt(answer.answer[0][1]) || 1;
          }
        } else if (typeof value === 'string' || typeof value === 'number') {
          orderDetails.quantity = parseInt(value) || 1;
        }
      } else if (text.includes('Size') || text.includes('Dimensions')) {
        if (typeof value === 'string') {
          orderDetails.dimensions = value;
        } else if (answer.answer && typeof answer.answer === 'object') {
          // Handle structured dimension answers
          const width = answer.answer.width || '';
          const height = answer.answer.height || '';
          if (width && height) {
            orderDetails.dimensions = `${width}x${height}`;
          }
        }
      } else if (text.includes('Special Instructions') || text.includes('Notes') || text.includes('Additional')) {
        orderDetails.specialInstructions = typeof value === 'string' ? value : '';
      } else if (text.includes('Rush') || text.includes('Expedited') || text.includes('24 hour') || text.includes('Production')) {
        const rushValue = typeof value === 'string' ? value : '';
        if (rushValue.toLowerCase().includes('super') || rushValue.toLowerCase().includes('24')) {
          orderDetails.productionOption = 'Super Rush 24 hrs';
          orderDetails.rushService = true;
        } else if (rushValue.toLowerCase().includes('yes') || rushValue.toLowerCase().includes('rush') || rushValue.toLowerCase().includes('1-2')) {
          orderDetails.productionOption = 'Rush 1-2 Days';
          orderDetails.rushService = true;
        } else {
          orderDetails.productionOption = 'Standard 2-3 Days';
          orderDetails.rushService = false;
        }
      } else if (text.includes('Pre-cut') || text.includes('Precut')) {
        const precutValue = typeof value === 'string' ? value : '';
        orderDetails.precut = precutValue.toLowerCase().includes('yes');
      } else if (text.includes('Gang Sheet')) {
        const gangValue = typeof value === 'string' ? value : '';
        orderDetails.gangSheetRequired = gangValue.toLowerCase().includes('yes');
      } else if (text.includes('Weeding')) {
        const weedingValue = typeof value === 'string' ? value : '';
        orderDetails.addWeeding = weedingValue.toLowerCase().includes('yes');
      } else if (text.includes('Transfer Tape') || text.includes('Application Tape')) {
        const tapeValue = typeof value === 'string' ? value : '';
        orderDetails.transferTape = tapeValue.toLowerCase().includes('yes');
      } else if (text.includes('Shipping Method') || text.includes('Shipping Option') || text.includes('Delivery Method') || text.includes('Fulfillment')) {
        const shipValue = typeof value === 'string' ? value : '';
        // Map to allowed values with more comprehensive detection
        const shipLower = shipValue.toLowerCase();

        // First check if it's a Will Call order
        if (shipLower.includes('will call') || 
            shipLower.includes('pickup') || 
            shipLower.includes('pick up') ||
            shipLower.includes('local') ||
            shipLower.includes('in store') ||
            shipLower.includes('in-store')) {
          orderDetails.fulfillmentOption = 'Will Call';
          orderDetails.shippingOption = null; // No shipping needed
        } else {
          // It's a Ship order, determine speed
          orderDetails.fulfillmentOption = 'Ship';

          if (shipLower.includes('overnight') || 
              shipLower.includes('next day') || 
              shipLower.includes('next-day') ||
              shipLower.includes('1 day') ||
              shipLower.includes('1-day') ||
              shipLower.includes('priority overnight')) {
            orderDetails.shippingOption = 'Overnight';
          } else if (shipLower.includes('express') || 
                     shipLower.includes('2 day') || 
                     shipLower.includes('2-day') ||
                     shipLower.includes('second day') ||
                     shipLower.includes('expedited')) {
            orderDetails.shippingOption = 'Express';
          } else {
            orderDetails.shippingOption = 'Ground';
          }
        }
      } else if (text.includes('Payment') || text.includes('How will you pay')) {
        orderDetails.paymentMethod = typeof value === 'string' ? value : 'Pending';
      } else if (text.includes('How did you hear') || text.includes('Referral')) {
        orderDetails.referralSource = typeof value === 'string' ? value : '';
      } else if (text.includes('Marketing') || text.includes('Newsletter')) {
        const marketingValue = typeof value === 'string' ? value : '';
        customer.emailOptIn = marketingValue.toLowerCase().includes('yes');
      } else if (text.includes('SMS') || text.includes('Text')) {
        const smsValue = typeof value === 'string' ? value : '';
        customer.smsOptIn = smsValue.toLowerCase().includes('yes');
      } else if (text.includes('Tax Exempt')) {
        const taxValue = typeof value === 'string' ? value : '';
        customer.taxExempt = taxValue.toLowerCase().includes('yes');
      }

      // File uploads - Updated to extract filenames
      if ((answer.type === 'control_fileupload' || text.includes('Upload') || text.includes('File')) && answer.answer) {
        const files = Array.isArray(answer.answer) ? answer.answer : [answer.answer];
        files.forEach(fileUrl => {
          if (fileUrl && typeof fileUrl === 'string') {
            const fileName = extractFilenameFromUrl(fileUrl);
            console.log(`Adding file: ${fileName} from URL: ${fileUrl}`);
            orderDetails.fileData.push({
              url: fileUrl,
              name: fileName
            });
          }
        });
      }
    }

    // After parsing, ensure shipping has customer info
    if (customer.name && !shipping.name) {
      shipping.name = customer.name;
      shipping.firstName = customer.firstName;
      shipping.lastName = customer.lastName;
    }
    if (customer.company) {
      shipping.company = customer.company;
    }
    if (customer.phone && !shipping.phone) {
      shipping.phone = customer.phone;
    }

    // Update billing to match shipping
    billing = {...shipping};

    console.log(`Found ${orderDetails.fileData.length} files for order ${orderId}`);
    console.log(`Customer data: name="${customer.name}", email="${customer.email}", phone="${customer.phone}"`);

    // Detect product type with all available data
    const detectedProductType = detectProductType(orderDetails.productType, formName, answers, formId);

    // Determine facility
    const facility = determineFacility(formName, shipping.state, detectedProductType);

    // Update shipping method to shipping/fulfillment options
    shipping.method = orderDetails.shippingOption;  // DEPRECATED - use fulfillmentOption and shippingOption instead

    // Extract order tags
    const orderTags = [];

    if (orderDetails.rushService) orderTags.push('rush');
    if (orderDetails.precut) orderTags.push('precut');
    if (orderDetails.gangSheetRequired || detectedProductType.includes('Gang Sheet')) orderTags.push('gang-sheet');

    // Add form-specific tags
    const form = (formName || '').toLowerCase();
    if (form.includes('wholesale') || form.includes('b2b')) orderTags.push('wholesale');
    if (form.includes('sample')) orderTags.push('sample');
    if (form.includes('custom')) orderTags.push('custom');
    if (form.includes('bulk')) orderTags.push('bulk');

    // Add referral tag if applicable
    if (orderDetails.referralSource) {
