// Parse Jiffy Order with Attachment Storage
// This is the enhanced version that properly structures attachment data

// Helper function to extract actual filename from URL
function extractFilenameFromUrl(url) {
  if (!url) return '';
  // Handle Jiffy URLs with filename parameter
  const filenameMatch = url.match(/filename=([^&]+)/);
  if (filenameMatch) {
    return decodeURIComponent(filenameMatch[1]);
  }
  // Fallback to last part of URL
  const urlParts = url.split('/');
  const filename = urlParts[urlParts.length - 1];
  return filename.split('?')[0];
}

// Extract dimensions and return width, height, and formatted string
function extractDimensionsFromFilename(filename) {
  if (!filename) return { width: 0, height: 0, dimensions: '' };
  // Match patterns like "22.4x48.47in" or "22.0x5.95in"
  const match = filename.match(/(\d+(?:\.\d+)?)\s*x\s*(\d+(?:\.\d+)?)/i);
  if (match) {
    const width = parseFloat(match[1]);
    const height = parseFloat(match[2]);
    return {
      width: width,
      height: height,
      dimensions: `${width}x${height}`
    };
  }
  return { width: 0, height: 0, dimensions: '' };
}

// Detect product type from filename
function detectProductTypeFromFilename(filename) {
  const filenameLower = filename.toLowerCase();
  if (filenameLower.includes('sublimation')) {
    return 'Sublimation Gang Sheet';
  } else if (filenameLower.includes('uv')) {
    return 'UV Gang Sheet';
  } else if (filenameLower.includes('dtf') || filenameLower.includes('transfers')) {
    return 'DTF Gang Sheet';
  }
  return 'Gang Sheet'; // Default
}

// Generate proper title from filename
function generateTitleFromFilename(filename, productType) {
  const dims = extractDimensionsFromFilename(filename);
  if (dims.dimensions) {
    return `${productType} ${dims.dimensions}`;
  }
  // Fallback to cleaned filename
  return filename.replace(/\.[^/.]+$/, '').replace(/-/g, ' '); // Remove extension and replace dashes
}

// Helper function to parse formatted text for additional data
function parseFormattedText(text) {
  const data = {
    orderType: '',
    precutQuantity: 0,
    totalPrintLength: ''
  };

  // Extract order type
  const orderTypeMatch = text.match(/ORDER TYPE:\s*(.+?)(?:\n|$)/i);
  if (orderTypeMatch) {
    data.orderType = orderTypeMatch[1].trim();
  }

  // Extract precut quantity
  const precutMatch = text.match(/Pre-cut Quantity:\s*(\d+)/i);
  if (precutMatch) {
    data.precutQuantity = parseInt(precutMatch[1]);
  }

  // Extract total print length
  const lengthMatch = text.match(/Total Print Length:\s*([\d.]+)\s*inches/i);
  if (lengthMatch) {
    data.totalPrintLength = lengthMatch[1];
  }

  return data;
}

