// Store Record IDs - Fixed to use Parse Jiffy data
// Get the ORIGINAL parsed data that contains orders and items
const parsedData = $('Fix Item Counts').all();

// Get Airtable responses
const orderResponses = $('Upsert Orders').all();
const customerResponses = $('Upsert Customers').all();

console.log(`Processing ${parsedData.length} parsed orders`);
console.log('First parsed data:', parsedData[0]?.json);

// Build lookup maps for Airtable records
const orderMap = new Map();
const customerMap = new Map();

// Map orders by Order ID
orderResponses.forEach(response => {
  if (!response.json.error && response.json.fields) {
    const orderId = response.json.fields['Order ID'];
    if (orderId) {
      orderMap.set(orderId, response);
    }
  }
});

// Map customers by email
customerResponses.forEach(response => {
  if (!response.json.error && response.json.fields) {
    const email = response.json.fields['Email'];
    if (email) {
      customerMap.set(email.toLowerCase(), response);
    }
  }
});

// Process the PARSED data, not the Airtable responses
return parsedData.map((item, index) => {
  const orderData = item.json.order || {};
  const items = item.json.items || [];
  const orderId = orderData.orderId;

  console.log(`Processing order ${orderId} with ${items.length} items`);

  // Find the Airtable records
  let orderRecord = orderMap.get(orderId);
  let customerRecord = null;

  // Try to find customer by email
  const customerEmail = orderData.source === 'jiffy' ? 
    'orders@jiffy.com' : 
    (orderData.customer?.email || '');

  if (customerEmail) {
    customerRecord = customerMap.get(customerEmail.toLowerCase());
  }

  // Return the parsed data WITH the Airtable record IDs
  return {
    json: {
      orderRecordId: orderRecord?.json?.id || null,
      customerRecordId: customerRecord?.json?.id || null,
      orderId: orderId,
      order: orderData,  // This is the actual order data
      items: items,      // These are the actual items
      _debug: {
        hasOrderRecord: !!orderRecord,
        hasCustomerRecord: !!customerRecord,
        itemCount: items.length,
        source: orderData.source || 'unknown'
      }
    }
  };
});

// Add Record IDs to Items - Fixed Version with Cutline Attachments
const inputData = $input.all();
const results = [];
let processedItemCount = 0;
let skippedCount = 0;
let errorCount = 0;

console.log(`Processing ${inputData.length} order inputs`);
console.log('First input structure:', JSON.stringify(inputData[0]?.json, null, 2));

// Helper function to validate file URL
function isValidFileUrl(url) {
  if (!url || typeof url !== 'string') return false;

  try {
    const urlObj = new URL(url);
    return urlObj.protocol === 'http:' || urlObj.protocol === 'https:';
  } catch {
    return false;
  }
}

// Helper function to extract filename from URL
function extractFilenameFromUrl(url) {
  if (!url) return 'untitled';

  try {
    // Check for filename parameter in Jiffy URLs
    const filenameMatch = url.match(/filename=([^&]+)/);
    if (filenameMatch) {
      return decodeURIComponent(filenameMatch[1]);
    }

    // Remove query parameters
    const cleanUrl = url.split('?')[0];
    // Get the last part of the path
    const parts = cleanUrl.split('/');
    const filename = parts[parts.length - 1] || 'untitled';
    // Decode URI component
    return decodeURIComponent(filename);
  } catch (e) {
    return 'untitled';
  }
}

// Process each order
for (let index = 0; index < inputData.length; index++) {
  const input = inputData[index];

  try {
    // Check for errors
    if (input.json.error) {
      console.log(`Skipping order at index ${index} due to error: ${input.json.error}`);
      results.push({ json: input.json });
      errorCount++;
      continue;
    }

    // Extract required data
    const orderRecordId = input.json.orderRecordId;
    const customerRecordId = input.json.customerRecordId;
    const orderId = input.json.orderId;
    const orderData = input.json.order || {};
    const items = input.json.items;

    // Validate required fields
    if (!orderRecordId) {
      console.error(`Missing orderRecordId at index ${index}`);
      results.push({
        json: {
          error: 'Missing orderRecordId',
          errorType: 'validation_error',
          index,
          inputData: input.json
        }
      });
      errorCount++;
      continue;
    }

    if (!orderId) {
      console.error(`Missing orderId at index ${index}`);
      results.push({
        json: {
          error: 'Missing orderId',
          errorType: 'validation_error',
          index,
          inputData: input.json
        }
      });
      errorCount++;
      continue;
    }

    // Check if items exist and is an array
    if (!items || !Array.isArray(items)) {
      console.warn(`No items array found for order ${orderId} at index ${index}`);

      // Instead of skipping, create a placeholder item
      const placeholderItem = {
        json: {
          // Order fields
          'Order': [orderRecordId],
          'Order ID': orderId,
          'Customer': customerRecordId ? [customerRecordId] : [],

          // Item identifiers
          'Line Item ID': `${orderId}-placeholder`,
          'Product ID': '',
          'Variant ID': '',
          'SKU': 'NO-ITEMS',

          // Basic info
          'Title': 'No Items Found',
          'Name': 'Placeholder - Check Order Data',
          'Qty': 0,
          'Price': 0,

          // File info
          'File URL': '',
          'Original File URL': '',
          'File Name': 'no-file',
          'Needs File?': true,

          // Status
          'Item Status': 'Error - No Items',
          'Artwork QC': 'Not Started',
          'QC Notes': 'No items found in order data',

          // Metadata
          'Meta (JSON)': JSON.stringify({
            error: 'No items array in input',
            orderSource: orderData.source || 'unknown',
            timestamp: new Date().toISOString()
          }),

          // Processing info
          'Processed At': new Date().toISOString()
        }
      };

      results.push(placeholderItem);
      skippedCount++;
      continue;
    }

    if (items.length === 0) {
      console.warn(`Empty items array for order ${orderId}`);
      skippedCount++;
      continue;
    }

    console.log(`Processing ${items.length} items for order ${orderId} (${orderData.source || 'unknown source'})`);

    // Get shipping address from order data
    const shipping = orderData.shipping || {};
    const shippingCountry = shipping.country || shipping.countryCode || '';
    const shippingState = shipping.state || shipping.provinceCode || '';

    // Process each item
    items.forEach((item, itemIndex) => {
      // IMPORTANT: Extract cutline references from _attachments
      let cutlineAttachments = [];
      if (item._attachments && item._attachments.cutlineRefs) {
        cutlineAttachments = item._attachments.cutlineRefs;
      }

      // Get cutline references - handle different data structures
      let cutlineReferences = [];

      // Check various possible locations for cutline references
      if (item.cutlineReferences && Array.isArray(item.cutlineReferences)) {
        cutlineReferences = item.cutlineReferences;
      } else if (item.meta?.jiffy?.cutlineReferences) {
        cutlineReferences = item.meta.jiffy.cutlineReferences;
      } else if (orderData.meta?.jiffy?.cutlineReferences) {
        // Use order-level cutline references if item doesn't have any
        cutlineReferences = orderData.meta.jiffy.cutlineReferences || [];
      }

      // Create an array of matched cutline reference filenames
      const matchedCutlineReferences = cutlineReferences.map(ref => 
        ref.filename || ref.url || ''
      ).filter(Boolean);

      // Determine item status based on file validation
      // For Jiffy orders, check if file URL exists and contains jiffy.com
      let itemStatus = item.itemStatus || 'Pending Art';
      const hasJiffyFile = item.fileUrl && item.fileUrl.includes('jiffy.com');

      if (!item.needsFile && (isValidFileUrl(item.fileUrl) || hasJiffyFile)) {
        itemStatus = 'Ready for QC';
      } else if (item.needsFile || !item.fileUrl) {
        itemStatus = 'Pending Art';
      }

      // Calculate dimensions - handle both numeric and string inputs
      const width = parseFloat(item.width) || 0;
      const height = parseFloat(item.height) || 0;
      const dimensions = width && height ? `${width}" x ${height}"` : (item.dimensions || item.originalDimensions || '');

      // Build the item record
      results.push({
        json: {
          // Linked records
          'Order': [orderRecordId],
          'Order ID': orderId,
          'Customer': customerRecordId ? [customerRecordId] : [],

          // Item identifiers
          'Line Item ID': item.lineItemId || item.id || `${orderId}-${itemIndex}`,
          'Product ID': item.productId || '',
          'Variant ID': item.variantId || '',
          'SKU': item.sku || '',

          // IMPORTANT: Add itemUid for matching in Process Cutline Attachments
          'itemUid': item.itemUid || `${orderId}-${itemIndex + 1}`,

          // Basic info
          'Title': item.title || item.name || 'Untitled Item',
          'Name': item.name || item.title || 'Untitled Item',
          'Qty': item.qty || 1,
          'Fulfillable Quantity': item.fulfillableQuantity || item.qty || 1,
          'Price': 0, // Set to 0 for Jiffy orders as requested

          // File info
          'File URL': item.fileUrl || '',
          'Original File URL': item.originalFileUrl || item.fileUrl || '',
          'File Name': item.fileName || item.filename || extractFilenameFromUrl(item.fileUrl) || 'no-file',
          'Needs File?': item.needsFile !== undefined ? item.needsFile : !isValidFileUrl(item.fileUrl),
          'File Validated': isValidFileUrl(item.fileUrl) || (item.fileUrl && item.fileUrl.includes('jiffy.com')),

          // Dimensions
          'Width': width,
          'Height': height,
          'Original Dimensions': dimensions,

          // Product details
          'Product Type': item.productType || 'DTF Transfers',
          'Application': item.application || '',
          'White Ink': item.whiteInk || '',
          'Underbase': item.underbase || '',

          // Shipping
          'Country': shippingCountry,
          'State': shippingState,

          // Fulfillment
          'Vendor': item.vendor || '',
          'Fulfillment Service': item.fulfillmentService || 'manual',
          'Fulfillment Status': item.fulfillmentStatus || null,

          // Status
          'Item Status': itemStatus,
          'Artwork QC': item.artworkQC || 'Pending',
          'QC Notes': item.qcNotes || '',

          // Financial
          'Taxable': item.taxable !== undefined ? item.taxable : false,
          'Gift Card': item.giftCard || false,

          // Precut info
          'Pre-cut?': item.isPrecut || false,
          'Precut Qty': item.precutQty || item.precutQuantity || 0,

          // Cutline references
          'Cutline References': matchedCutlineReferences.join(', '),

          // IMPORTANT: Store cutline attachments for later processing
          'cutlineAttachments': cutlineAttachments,

          // Facility
          'Facility': item.facility || null,

          // Metadata - FIXED to use correct order source
          'Meta (JSON)': JSON.stringify({
            cutlineReferences: matchedCutlineReferences,
            orderSource: item.source || orderData.source || 'jiffy', // Use item source first, then order source, default to 'jiffy'
            processedAt: new Date().toISOString()
          }),

          // Timestamps
          'Processed At': new Date().toISOString()
        }
      });

      processedItemCount++;
    });

    console.log(`Processed ${items.length} items for order ${orderId}`);

  } catch (error) {
    console.error(`Error processing order at index ${index}:`, error.message);
    results.push({
      json: {
        error: error.message,
        errorType: 'item_processing_error',
        index,
        orderId: input.json.orderId || 'unknown',
        orderSource: input.json.order?.source || 'unknown',
        timestamp: new Date().toISOString(),
        inputData: input.json
      }
    });
    errorCount++;
  }
}

console.log(`Processing complete: ${processedItemCount} items processed from ${inputData.length} orders, ${skippedCount} skipped, ${errorCount} errors`);

// If no results were generated, return diagnostic information
if (results.length === 0) {
  console.warn('No items processed - returning diagnostic information');

  const diagnostics = inputData.map((item, idx) => ({
    index: idx,
    hasOrderRecordId: !!item.json.orderRecordId,
    hasOrderId: !!item.json.orderId,
    hasOrder: !!item.json.order,
    orderSource: item.json.order?.source || 'unknown',
    hasItems: !!item.json.items,
    itemsIsArray: Array.isArray(item.json.items),
    itemCount: Array.isArray(item.json.items) ? item.json.items.length : 0,
    hasError: !!item.json.error,
    errorMessage: item.json.error || null
  }));

  return [{
    json: {
      message: 'No valid items to process',
      summary: {
        totalOrders: inputData.length,
        itemsProcessed: processedItemCount,
        ordersSkipped: skippedCount,
        errors: errorCount
      },
      diagnostics: diagnostics,
      timestamp: new Date().toISOString(),
      troubleshooting: {
        suggestion: 'Check if Store Record IDs is passing order and items data correctly',
        expectedStructure: {
          orderRecordId: 'string (Airtable record ID)',
          orderId: 'string (Order ID)',
          order: 'object with source property',
          items: 'array of item objects'
        }
      }
    }
  }];
}

return results;

// Get all items that were just created/updated in Airtable
const items = $input.all();
const fileRecords = [];

// Helper function to extract filename from URL
function extractFilenameFromUrl(url) {
  if (!url) return 'Untitled';

  try {
    // Extract the last part of the URL path
    const urlParts = url.split('/');
    const filename = urlParts[urlParts.length - 1];

    // Remove query parameters if present
    return filename.split('?')[0] || 'Untitled';
  } catch (e) {
    return 'Untitled';
  }
}

// Helper function to extract file format from URL
function extractFileFormat(url) {
  if (!url) return 'PNG';

  try {
    // Extract filename first
    const filename = extractFilenameFromUrl(url);

    // Get extension from filename
    const parts = filename.split('.');
    if (parts.length > 1) {
      return parts[parts.length - 1].toUpperCase();
    }

    // Fallback: try to extract from URL directly
    const urlParts = url.toLowerCase().split('.');
    if (urlParts.length > 1) {
      const ext = urlParts[urlParts.length - 1].split('?')[0]; // Remove query params
      return ext.toUpperCase();
    }

    return 'PNG'; // Default fallback
  } catch (e) {
    return 'PNG';
  }
}

// Helper function to determine if filename is a default product name
function isDefaultProductName(name) {
  if (!name) return true;

  const defaultNames = [
    'DTF Transfer Online Gang Sheet Builder PRO for Clothing',
    'DTF Transfer Online Gang Sheet Builder PRO',
    'Gang Sheet Builder',
    'Preview_preview',
    'Preview',
    'Processed',
    'Untitled'
  ];

  return defaultNames.some(defaultName => 
    name.toLowerCase().includes(defaultName.toLowerCase())
  );
}

// Helper function to get appropriate filename
function getFileName(itemFields, url, type = 'main') {
  const fieldName = itemFields['File Name'] || itemFields['Title'] || '';

  // If it's a default product name, extract from URL
  if (isDefaultProductName(fieldName)) {
    const urlFilename = extractFilenameFromUrl(url);

    // Add suffix based on type if needed
    if (type === 'processed' && !urlFilename.includes('300dpi')) {
      return urlFilename.replace(/\.(png|jpg|jpeg|pdf)$/i, '_300dpi.$1');
    } else if (type === 'preview' && !urlFilename.includes('preview')) {
      return urlFilename.replace(/\.(png|jpg|jpeg|pdf)$/i, '_preview.$1');
    }

    return urlFilename;
  }

  // Otherwise use the field name with appropriate suffix
  if (type === 'processed') {
    return `${fieldName}_300dpi`;
  } else if (type === 'preview') {
    return `${fieldName}_preview`;
  }

  return fieldName;
}

// Process items using for...of loop to allow continue
for (const item of items) {
  let itemRecordId = null;
  let orderRecordId = null;

  try {
    // Skip error items
    if (item.error || item.message) {
      console.log(`Skipping item with error: ${item.message || item.error}`);
      continue; // FIXED: Use continue instead of return
    }

    itemRecordId = item.json.id;
    const itemFields = item.json.fields;

    // FIXED: Get orderRecordId from the Airtable response
    // The "Orders" field in the Airtable response contains the linked order record ID
    if (itemFields['Orders'] && Array.isArray(itemFields['Orders']) && itemFields['Orders'].length > 0) {
      orderRecordId = itemFields['Orders'][0];
    }

    // Log for debugging if orderRecordId is still missing
    if (!orderRecordId) {
      console.warn(`No orderRecordId found for item ${itemRecordId}. Available fields:`, Object.keys(itemFields));
    }

    // Log for debugging
    console.log(`Processing item ${itemRecordId}, orderRecordId: ${orderRecordId}`);

    // Check if this item has files to create
    if (itemFields['File URL'] && !itemFields['Needs File']) {

      // Build the main file record
      fileRecords.push({
        json: {
          "File Name": getFileName(itemFields, itemFields['File URL'], 'main'),
          "URL": itemFields['File URL'],
          "Attachment": [{ "url": itemFields['File URL'] }],
          "Linked Item": [itemRecordId],
          "Orders": orderRecordId ? [orderRecordId] : [], // Use the correctly extracted orderRecordId
          "DPI": itemFields['File DPI'] || 300,
          "Quality Control Status": itemFields['Artwork QC'] === 'Approved' ? 'Approved' : 'Pending',
          "Meta (JSON)": JSON.stringify({
            source: itemFields['Meta (JSON)'] ? JSON.parse(itemFields['Meta (JSON)']).source : 'unknown',
            originalUrl: itemFields['Original File URL'] || itemFields['File URL'],
            processedUrl: itemFields['Processed File URL'],
            previewUrl: itemFields['Preview URL'],
            format: extractFileFormat(itemFields['File URL']), // FIXED: Extract from URL
            dimensions: itemFields['File Dimensions'] || itemFields['Dimensions (in)'],
            orderRecordId: orderRecordId,
            colorSpace: itemFields['File Color Mode'] || 'RGB'
          }),
          "Upload Date": new Date().toISOString(),
          "Original Dimensions": itemFields['File Dimensions'] || itemFields['Dimensions (in)'] || '',
          "File Format": extractFileFormat(itemFields['File URL']) // FIXED: Extract from URL
        }
      });

      // Create additional file record for processed URL if it exists and is different
      if (itemFields['Processed File URL'] && 
          itemFields['Processed File URL'] !== itemFields['File URL']) {
        fileRecords.push({
          json: {
            "File Name": getFileName(itemFields, itemFields['Processed File URL'], 'processed'),
            "URL": itemFields['Processed File URL'],
            "Attachment": [{ "url": itemFields['Processed File URL'] }],
            "Linked Item": [itemRecordId],
            "Orders": orderRecordId ? [orderRecordId] : [],
            "DPI": 300,
            "Quality Control Status": 'Approved',
            "Meta (JSON)": JSON.stringify({
              source: itemFields['Meta (JSON)'] ? JSON.parse(itemFields['Meta (JSON)']).source : 'unknown',
              type: 'processed',
              originalFile: itemFields['File URL'],
              orderRecordId: orderRecordId,
              colorSpace: 'RGB'
            }),
            "Upload Date": new Date().toISOString(),
            "File Format": extractFileFormat(itemFields['Processed File URL']), // FIXED: Extract from URL
            "Processed Date": new Date().toISOString()
          }
        });
      }

      // Create preview file record if it exists
      if (itemFields['Preview URL'] && 
          itemFields['Preview URL'] !== itemFields['File URL'] &&
          itemFields['Preview URL'] !== itemFields['Processed File URL']) {
        fileRecords.push({
          json: {
            "File Name": getFileName(itemFields, itemFields['Preview URL'], 'preview'),
            "URL": itemFields['Preview URL'],
            "Attachment": [{ "url": itemFields['Preview URL'] }],
            "Linked Item": [itemRecordId],
            "Orders": orderRecordId ? [orderRecordId] : [],
            "DPI": 72, // Preview images are typically lower DPI
            "Quality Control Status": 'Approved',
            "Meta (JSON)": JSON.stringify({
              source: itemFields['Meta (JSON)'] ? JSON.parse(itemFields['Meta (JSON)']).source : 'unknown',
              type: 'preview',
              originalFile: itemFields['File URL'],
              orderRecordId: orderRecordId,
              colorSpace: 'RGB'
            }),
            "Upload Date": new Date().toISOString(),
            "File Format": extractFileFormat(itemFields['Preview URL']) // FIXED: Extract from URL
          }
        });
      }
    }
  } catch (error) {
    console.error('Error preparing file record for item:', itemRecordId || 'unknown', error.message);
  }
}

// Log summary
console.log(`Prepared ${fileRecords.length} file records from ${items.length} items`);

return fileRecords.length > 0 ? fileRecords : [{ json: { message: 'No files to create' } }];

