// Check for Shipments - Process orders from Airtable
const items = $input.all();

return items.map(item => {
  const order = item.json;

  // Initialize shipment data
  let hasShippingLabel = false;
  let shippingLabelUrl = null;
  let cutlineReferences = [];
  let needsShipment = false;
  let shipmentReady = false;

  try {
    // Parse the Items Data (JSON) field - it's a stringified JSON
    const itemsData = JSON.parse(order['Items Data (JSON)'] || '[]');

    // Check each item for shipping label and cutline references
    itemsData.forEach(orderItem => {
      // Check for shipping label URL in the item
      if (orderItem.shippingLabelUrl) {
        hasShippingLabel = true;
        shippingLabelUrl = orderItem.shippingLabelUrl;
      }

      // Collect cutline references
      if (orderItem.cutlineReferences && Array.isArray(orderItem.cutlineReferences)) {
        cutlineReferences = [...cutlineReferences, ...orderItem.cutlineReferences];
      }

      // Also check in meta.jiffy if present
      if (orderItem.meta?.jiffy?.shippingLabelUrl) {
        hasShippingLabel = true;
        shippingLabelUrl = orderItem.meta.jiffy.shippingLabelUrl;
      }

      if (orderItem.meta?.jiffy?.cutlineReferences) {
        cutlineReferences = [...cutlineReferences, ...orderItem.meta.jiffy.cutlineReferences];
      }
    });

    // Remove duplicate cutline references
    cutlineReferences = cutlineReferences.filter((ref, index, self) => 
      index === self.findIndex(r => r.url === ref.url)
    );

    // Determine if order needs shipment
    const fulfillmentOption = order['Fulfillment Option'] || '';
    const orderStatus = order['Order Status'] || '';
    const fulfillmentStatus = order['Fulfillment Status'] || '';
    const source = order['Source'] || '';

    // Order needs shipment if:
    // 1. Fulfillment option is "Ship"
    // 2. Order is not yet fulfilled
    // 3. Has items to ship
    needsShipment = (
      fulfillmentOption.toLowerCase() === 'ship' &&
      orderStatus !== 'Canceled' &&
      orderStatus !== 'Refunded' &&
      fulfillmentStatus !== 'Fulfilled' &&
      itemsData.length > 0
    );

    // Shipment is ready if:
    // 1. Needs shipment
    // 2. Has shipping label (for Jiffy orders)
    // 3. Has shipping address
    shipmentReady = needsShipment && (
      (source.toLowerCase() === 'jiffy' && hasShippingLabel) ||
      (source.toLowerCase() !== 'jiffy')
    ) && order['Shipping Zip'];

  } catch (error) {
    console.error('Error parsing items data:', error.message);
  }

  // Return enriched order data
  return {
    json: {
      // Original order data
      ...order,

      // Shipment analysis
      shipment: {
        needsShipment,
        shipmentReady,
        hasShippingLabel,
        shippingLabelUrl,
        cutlineReferences,

        // Extract key shipping fields
        orderId: order['Order ID'],
        source: order['Source'],
        fulfillmentOption: order['Fulfillment Option'],
        shippingAddress: {
          name: order['Shipping Name'] || order['Customer Name'],
          address1: order['Shipping Address 1'],
          address2: order['Shipping Address 2'],
          city: order['Shipping City'],
          state: order['Shipping State'],
          zip: order['Shipping Zip'],
          country: order['Shipping Country'] || 'US',
          phone: order['Shipping Phone'] || order['Customer Phone']
        },

        // Status
        orderStatus: order['Order Status'],
        fulfillmentStatus: order['Fulfillment Status'],

        // Jiffy specific
        isJiffyOrder: order['Source']?.toLowerCase() === 'jiffy',
        jiffyPoNumber: order['Jiffy PO Number'],

        // Metadata
        checkedAt: new Date().toISOString()
      },

      // Quick flags for routing
      _needsShipment: needsShipment,
      _shipmentReady: shipmentReady,
      _hasShippingLabel: hasShippingLabel,
      _isJiffyOrder: order['Source']?.toLowerCase() === 'jiffy'
    }
  };
});

