const analysis = $json.analysis || {};
const fileRecord = $('Download File for Analysis').item.json;
const fileName = fileRecord.fields?.['File Name'] || 'Unknown';
const fileUrl = fileRecord.fields?.URL || '';

// Get order context from earlier nodes
let orderId = '';
let customerEmail = '';
let orderRecordId = '';

try {
  const orderContext = $('Store Record IDs').item.json;
  orderId = orderContext.orderId || '';
  customerEmail = orderContext.customerEmail || '';
  orderRecordId = orderContext.orderRecordId || '';
} catch (e) {
  console.log('Could not get order context');
}

const issues = [];
let qualityScore = 'Good';

// Check DPI
if (analysis.dpi && analysis.dpi < 300) {
  issues.push(`Low DPI: ${analysis.dpi} (recommended 300+)`);
  qualityScore = 'Poor';
} else if (analysis.dpi && analysis.dpi < 250) {
  issues.push(`Very low DPI: ${analysis.dpi} (minimum 300 required)`);
  qualityScore = 'Poor';
}

// Check dimensions
if (analysis.dimensions) {
  const { widthInches, heightInches } = analysis.dimensions;
  if (widthInches && widthInches > 22) {
    issues.push(`Width too large: ${widthInches}" (max 22")`);
    qualityScore = 'Poor';
  }
  if (heightInches && heightInches > 100) {
    issues.push(`Height too large: ${heightInches}" (max 100")`);
    qualityScore = 'Poor';
  }

  // Check for very small images
  if (widthInches && widthInches < 0.5) {
    issues.push(`Very small width: ${widthInches}" (may be too small to print)`);
    qualityScore = qualityScore === 'Good' ? 'Fair' : qualityScore;
  }
  if (heightInches && heightInches < 0.5) {
    issues.push(`Very small height: ${heightInches}" (may be too small to print)`);
    qualityScore = qualityScore === 'Good' ? 'Fair' : qualityScore;
  }
}

// Check file size (if available)
if (analysis.fileSize) {
  const fileSizeMB = analysis.fileSize / (1024 * 1024);
  if (fileSizeMB > 50) {
    issues.push(`Large file size: ${Math.round(fileSizeMB)}MB (may cause processing delays)`);
    qualityScore = qualityScore === 'Good' ? 'Fair' : qualityScore;
  }
}

// Check transparency for DTF (should have transparency)
if (analysis.hasTransparency === false && !fileName.toLowerCase().includes('gang')) {
  issues.push('No transparency detected - DTF transfers typically need transparent backgrounds');
  qualityScore = qualityScore === 'Good' ? 'Fair' : qualityScore;
}

// Check for other quality indicators
if (analysis.colorSpace && analysis.colorSpace !== 'RGB') {
  issues.push(`Color space: ${analysis.colorSpace} (RGB recommended for DTF)`);
}

if (analysis.bitDepth && analysis.bitDepth < 8) {
  issues.push(`Low bit depth: ${analysis.bitDepth}-bit (8-bit minimum recommended)`);
  qualityScore = 'Poor';
}

return [{
  json: {
    fileRecordId: fileRecord.id,
    fileName: fileName,
    fileUrl: fileUrl,
    orderId: orderId,
    customerEmail: customerEmail,
    orderRecordId: orderRecordId,
    hasIssues: issues.length > 0,
    issues: issues,
    analysis: analysis,
    needsReview: issues.length > 0,
    qualityScore: qualityScore,
    issueCount: issues.length,
    severity: qualityScore === 'Poor' ? 'High' : qualityScore === 'Fair' ? 'Medium' : 'Low'
  }
}];

