parameters.jsCode:
// Get the analysis results from Detect File Issues
const analysisResult = $input.all()[0].json;

// Find the corresponding Order Item record to update
// We need to find the item that has this file URL
const fileUrl = analysisResult.fileUrl;
const orderId = analysisResult.orderId;

// Get all order items that were created in this workflow
let orderItems = [];
try {
  orderItems = $('Upsert Order Items').all();
} catch (e) {
  console.log('Could not access order items node');
}

// Find the matching order item by file URL or order ID
const matchingItem = orderItems.find(item => {
  const itemFields = item.json.fields || {};
  const itemFileUrl = itemFields['File URL'] || itemFields['Original File URL'];
  const itemOrderId = itemFields['Orders'] || [];

  return itemFileUrl === fileUrl || 
         (orderId && itemOrderId.some(id => id.includes(orderId)));
});

if (!matchingItem) {
  console.log(`No matching order item found for file: ${fileUrl}`);
  return [{ json: { message: 'No matching order item found', skip: true } }];
}

const itemRecordId = matchingItem.json.id;
const qcStatus = analysisResult.hasIssues ? 'Fix Needed' : 'Approved';
const qcNotes = analysisResult.issues.length > 0 ? 
  `Automated analysis: ${analysisResult.issues.join('; ')}` : 
  'Automated analysis passed - no issues detected';

return [{
  json: {
    itemRecordId: itemRecordId,
    qcStatus: qcStatus,
    qcNotes: qcNotes,
    qualityScore: analysisResult.qualityScore,
    fileQcStatus: analysisResult.hasIssues ? 'Issues Detected' : 'Quality OK'
  }
}];

// Merge the analysis results back to continue normal workflow
// This ensures the Slack notification gets the order data it needs

// Get the original order data from earlier in the workflow
let orderData = {};
try {
  orderData = $('Store Record IDs').item.json;
} catch (e) {
  console.log('Could not get order data');
}

// Pass through the order data for the final Slack notification
return [{
  json: {
    ...orderData,
    imageAnalysisComplete: true,
    analysisTimestamp: new Date().toISOString()
  }
}];

// Format HTML node - processes Gmail HTML content and extracts Jiffy order information
// This version includes fix for pre-cut orders where anchor text equals the URL

// Get Google Drive share link from previous node
const googleDriveShareLink = $('Google Drive').first()?.json?.webContentLink || null;

// Helper function to extract filename from URL
function extractFilenameFromUrl(url) {
  if (!url) return '';

  try {
    // Look for filename parameter in URL
    const match = url.match(/filename=([^&]+)/);
    if (match && match[1]) {
      return decodeURIComponent(match[1]);
    }

    // Otherwise extract the last part of the URL path
    const urlParts = url.split('/');
    const filename = urlParts[urlParts.length - 1];
    return filename.split('?')[0] || '';
  } catch (e) {
    return url; // Return full URL if extraction fails
  }
}

// Extract basic order information from HTML
const extractBasicInfo = html => {
  const poMatch = html.match(/PO Number:\s*(\d+)/);
  const orderTypeMatch = html.match(/Order Type:\s*([^<]+)/);
  const shippingDateMatch = html.match(/Committed Shipping Date:\s*([^<]+)/);
  const addressMatch = html.match(/Delivery address:<\/p>\s*<p>([^<]+)<\/p>\s*<p>([^<]+)<\/p>\s*<p>([^<]+)<\/p>/);

  return {
    poNumber: poMatch ? poMatch[1] : '',
    orderType: orderTypeMatch ? orderTypeMatch[1].trim() : '',
    shippingDate: shippingDateMatch ? shippingDateMatch[1].trim() : '',
    addressParts: addressMatch ? {
      line1: addressMatch[1],
      line2: addressMatch[2],
      line3: addressMatch[3]
    } : null
  };
};

const parseAddress = (addressParts) => {
  if (!addressParts) return null;

  // Parse line3 for city, state, zip
  const cityStateZip = addressParts.line3.match(/^([^,]+),\s*([A-Z]{2})\s+(\d{5}(?:-\d{4})?)$/);

  return {
    name: addressParts.line1 || '',
    company: 'Jiffy',
    address1: addressParts.line2 || '',
    city: cityStateZip ? cityStateZip[1] : '',
    state: cityStateZip ? cityStateZip[2] : '',
    zip: cityStateZip ? cityStateZip[3] : '',
    country: 'US',
    countryCode: 'US'
  };
};

const extractFileInfo = html => {
  const links = [];
  const fileDetails = [];

  (html.match(/<a href=\"([^\"]+)\">([^<]+)<\/a>/g) || []).forEach(m => {
    const urlMatch = m.match(/<a href=\"([^\"]+)\">([^<]+)<\/a>/);
    if (urlMatch) {
      const url = urlMatch[1];
      const text = urlMatch[2];

      if (url.includes('jiffy.com')) {
        links.push(url);

        // Check if text contains dimensions (regular orders)
        if (text.includes('x') && /\d+/.test(text) && !text.startsWith('http')) {
          fileDetails.push({
            url: url,
            name: text,
            dimensions: text.match(/(\d+(?:\.\d+)?)\s*x\s*(\d+(?:\.\d+)?)/)?.[0] || ''
          });
        } else {
          // For pre-cut orders where text is the URL, extract dimensions from filename
          const filenameMatch = url.match(/filename=([^&]+)/);
          if (filenameMatch) {
            const filename = decodeURIComponent(filenameMatch[1]);
            const dimMatch = filename.match(/(\d+(?:\.\d+)?)\s*x\s*(\d+(?:\.\d+)?)\s*in/i);

            fileDetails.push({
              url: url,
              name: filename,
              dimensions: dimMatch ? `${dimMatch[1]}x${dimMatch[2]}` : ''
            });
          }
        }
      }
    }
  });

  // Also process cutline references
  (html.match(/<a href=\"([^\"]+imgix[^\"]+)\">([^<]+)<\/a>/g) || []).forEach(m => {
    const urlMatch = m.match(/<a href=\"([^\"]+)\">([^<]+)<\/a>/);
    if (urlMatch) {
      const url = urlMatch[1];
      const dlMatch = url.match(/dl=([^&]+)/);
      const filename = dlMatch ? decodeURIComponent(dlMatch[1]) : '';

      fileDetails.push({
        url: url,
        filename: filename || url.split('/').pop().split('?')[0],
        type: 'cutline_reference'
      });
    }
  });

  return { links, fileDetails };
};

const extractItemsFromSection = (sectionHtml) => {
  const items = [];
  const lines = sectionHtml.split(/<\/p>|<br\/>/);

  lines.forEach(line => {
    const itemMatch = line.match(/([^(]+)\((\d+)\)/);
    if (itemMatch) {
      const name = itemMatch[1].trim();
      const quantity = parseInt(itemMatch[2]);
      const dimMatch = name.match(/(\d+(?:\.\d+)?)\s*x\s*(\d+(?:\.\d+)?)/);

      items.push({
        name: name,
        quantity: quantity,
        dimensions: dimMatch ? dimMatch[0] : '',
        productType: line.includes('UV') ? 'UV DTF' : 'DTF Transfers'
      });
    }
  });

  return items;
};

const extractSectionInfo = html => {
  const sections = {};

  const preCutDTFMatch = html.match(/<p>Pre-Cut DTF<br\/>-----------<\/p>[\s\S]*?(?=<p>Pre-Cut UV DTF|$)/);
  if (preCutDTFMatch) {
    sections.preCutDTF = {
      raw: preCutDTFMatch[0].trim(),
      items: extractItemsFromSection(preCutDTFMatch[0])
    };
  }

  const preCutUVDTFMatch = html.match(/<p>Pre-Cut UV DTF<br\/>--------------<\/p>[\s\S]*?(?=$)/);
  if (preCutUVDTFMatch) {
    sections.preCutUVDTF = {
      raw: preCutUVDTFMatch[0].trim(),
      items: extractItemsFromSection(preCutUVDTFMatch[0])
    };
  }

  return sections;
};

const calculateOrderTotals = (items) => {
  const pricing = {
    'DTF Transfers': 0.05,
    'UV DTF': 0.08,
    'Pre-cut': 2.00
  };

  let subtotal = 0;

  items.forEach(item => {
    if (item.dimensions) {
      const [width, height] = item.dimensions.split('x').map(d => parseFloat(d));
      const sqInches = width * height;
      const basePrice = sqInches * (pricing[item.productType] || 0.05);
      const precutPrice = item.name.toLowerCase().includes('pre-cut') ? pricing['Pre-cut'] : 0;

      subtotal += (basePrice + precutPrice) * item.quantity;
    }
  });

  return {
    subtotal: subtotal,
    shipping: 0.00,
    tax: 0,
    total: subtotal
  };
};

// Process each item
return items.map(item => {
  const fullHtml = item.json.html || item.json.textAsHtml || '';
  const email = item.json;

  // Extract just the content inside the wrapper div to avoid CSS interference
  const wrapperMatch = fullHtml.match(/<div class="wrapper">([\s\S]*?)<\/div>\s*<\/body>/);
  const html = wrapperMatch ? wrapperMatch[1] : fullHtml;

  // Extract all information
  const basicInfo = extractBasicInfo(html);
  const fileInfo = extractFileInfo(html);
  const sections = extractSectionInfo(html);
  const shippingAddress = parseAddress(basicInfo.addressParts);

  // Combine all items from different sections
  const allItems = [
    ...(sections.preCutDTF?.items || []),
    ...(sections.preCutUVDTF?.items || [])
  ];

  // Calculate totals
  const totals = calculateOrderTotals(allItems);

  // Create the order ID
  const orderId = `JIFFY-${basicInfo.poNumber}`;
  const submissionId = `jiffy-${Date.now()}`;

  // Determine rush service
  const rushService = basicInfo.orderType.toLowerCase().includes('rush') ? 'Rush 1-2 Days' : null;

  // Build shipping label info
  const shippingLabelInfo = googleDriveShareLink ? 
    { url: googleDriveShareLink, filename: extractFilenameFromUrl(googleDriveShareLink) } : 
    null;

  // Parse sections and extract more detailed info
  const parseSections = (html) => {
    const result = {
      orderType: '',
      gangSheetUrls: [],
      cutlineReferences: [],
      precutQuantity: 0,
      totalPrintLength: ''
    };

    // Extract order type
    const orderTypeMatch = html.match(/Order Type:\s*([^<]+)/);
    if (orderTypeMatch) {
      result.orderType = orderTypeMatch[1].trim();
    }

    // Extract pre-cut quantities
    const precutMatches = html.match(/Pre-cut quantity:\s*(\d+)/gi);
    if (precutMatches) {
      precutMatches.forEach(match => {
        const qtyMatch = match.match(/(\d+)/);
        if (qtyMatch) {
          result.precutQuantity += parseInt(qtyMatch[1]);
        }
      });
    }

    // Extract total print lengths and sum them
    const lengthMatches = html.match(/Total Print Length:\s*([\d.]+)\s*inches/gi);
    if (lengthMatches) {
      let totalLength = 0;
      lengthMatches.forEach(match => {
        const lenMatch = match.match(/([\d.]+)/);
        if (lenMatch) {
          totalLength += parseFloat(lenMatch[1]);
        }
      });
      result.totalPrintLength = totalLength.toString();
    }

    // Extract gang sheet URLs
    const gangSheetUrls = [];
    const gangSheetMatches = html.match(/<a href="(https:\/\/www\.jiffy\.com\/dtf_gang_sheets[^"]+)">/g);
    if (gangSheetMatches) {
      gangSheetMatches.forEach(match => {
        const urlMatch = match.match(/href="([^"]+)"/);
        if (urlMatch) {
          gangSheetUrls.push(urlMatch[1]);
        }
      });
    }
    result.gangSheetUrls = gangSheetUrls;

    // Extract cutline references
    const cutlineRefs = [];
    const cutlineMatches = html.match(/<a href="(https:\/\/jiffy-transfers\.imgix\.net[^"]+)">/g);
    if (cutlineMatches) {
      cutlineMatches.forEach(match => {
        const urlMatch = match.match(/href="([^"]+)"/);
        if (urlMatch) {
          const url = urlMatch[1];
          // Extract filename from dl parameter
          const dlMatch = url.match(/dl=([^&]+)/);
          const filename = dlMatch ? decodeURIComponent(dlMatch[1]) : '';

          cutlineRefs.push({
            url: url,
            filename: filename || url.split('/').pop().split('?')[0],
            type: 'cutline_reference'
          });
        }
      });
    }
    result.cutlineReferences = cutlineRefs;

    return result;
  };

  const parsedSections = parseSections(html);

  // Get all unique gang sheet URLs
  const allGangSheetUrls = [...new Set([...fileInfo.links, ...parsedSections.gangSheetUrls])];

  // Get gang sheet files (non-reference files)
  const gangSheetFiles = fileInfo.fileDetails.filter(f => f.type !== 'cutline_reference');

  // Get cutline references
  const cutlineReferences = fileInfo.fileDetails.filter(f => f.type === 'cutline_reference');

  // Create output
  const output = {
    // HTML content
    html: html,
    html_original: email.html || '',

    // Order identifiers
    orderId: orderId,
    submissionId: submissionId,
    source: 'jiffy',
    poNumber: basicInfo.poNumber,
    jiffyPoNumber: basicInfo.poNumber,

    // Order details
    orderType: parsedSections.orderType || basicInfo.orderType,
    rushService: rushService,
    productTypes: [],
    productTypesString: '',

    // Customer info
    customer: {
      email: 'orders@jiffy.com',
      name: 'Jiffy',
      company: 'Jiffy',
      phone: '',
      isB2B: true
    },

    // Shipping info
    shipping: shippingAddress,
    shippingDate: basicInfo.shippingDate,

    // Financial
    financial: totals,
    paymentStatus: 'Paid',

    // Items (from parsed sections)
    items: allItems,
    itemCount: allItems.length,

    // Files and gang sheets
    files: gangSheetFiles,
    gangSheetUrls: allGangSheetUrls,
    cutlineReferences: cutlineReferences,

    // File status
    needsFile: allGangSheetUrls.length === 0,
    fileStatus: allGangSheetUrls.length > 0 ? 'All Files Received' : 'Pending Files',

    // Shipping label
    shipping_label_url: shippingLabelInfo?.url || null,
    shipping_label_google_drive: shippingLabelInfo?.url || null,
    shippingLabel: shippingLabelInfo,

    // Additional metadata
    meta: {
      jiffy: {
        poNumber: basicInfo.poNumber,
        orderType: parsedSections.orderType || basicInfo.orderType,
        productTypes: [],
        shippingLabel: shippingLabelInfo,
        cutlineReferences: cutlineReferences,
        totalPrintLength: parsedSections.totalPrintLength,
        precutQuantity: parsedSections.precutQuantity,
        shippingDate: basicInfo.shippingDate
      }
    },

    // Formatted text summary
    formatted_text: `ORDER SUMMARY
=============
PO NUMBER: ${basicInfo.poNumber}
ORDER TYPE: ${parsedSections.orderType || basicInfo.orderType}
QUALITY CHECK: ${html.includes('Requires Quality Check:') ? (html.includes('Yes') ? 'Yes' : 'No') : 'No'}
SHIPPING DATE: ${basicInfo.shippingDate}
DELIVERY ADDRESS:
${shippingAddress ? `${shippingAddress.name}
${shippingAddress.address1}
${shippingAddress.city}, ${shippingAddress.state} ${shippingAddress.zip}` : 'N/A'}

ITEMS: ${allItems.length} total items
PRODUCT TYPES: ${allItems.map(i => i.productType).filter((v, i, a) => a.indexOf(v) === i).join(', ')}
SUBTOTAL: $${totals.subtotal.toFixed(2)}
SHIPPING: $${totals.shipping.toFixed(2)}
TOTAL: $${totals.total.toFixed(2)}

${parsedSections.precutQuantity > 0 ? `PRE-CUT QUANTITY: ${parsedSections.precutQuantity}\n` : ''}${parsedSections.totalPrintLength ? `TOTAL PRINT LENGTH: ${parsedSections.totalPrintLength} inches\n` : ''}

GANG SHEET FILES:
${allGangSheetUrls.map(url => `- ${url}`).join('\n')}

${cutlineReferences.length > 0 ? `
CUTLINE REFERENCES:
${cutlineReferences.map(ref => `- ${ref.url}`).join('\n')}` : ''}`
  };

  return { json: output };
});

// Fix Item Count and add Precut Qty to Items
const items = $input.all();

return items.map(item => {
  const data = item.json;

  // Deep clone to avoid modifying original
  const output = JSON.parse(JSON.stringify(data));

  // Fix the order data
  if (output.order) {
    // Parse Items Data JSON to get actual item count
    let actualItemCount = 0;
    if (data.items && Array.isArray(data.items)) {
      actualItemCount = data.items.length;
    }

    // Update the item count in summary
    if (output.order.summary) {
      output.order.summary.itemCount = actualItemCount;
    }

    // Get precut quantity from order meta
    let orderPrecutQty = 0;
    if (output.order.meta?.jiffy?.precutQuantity !== undefined) {
      orderPrecutQty = output.order.meta.jiffy.precutQuantity;
    }

    // Fix items array - add precutQty to each item
    if (output.items && Array.isArray(output.items)) {
      output.items = output.items.map(orderItem => {
        // Determine precut quantity for this item
        let itemPrecutQty = 0;

        // Check if this item is precut
        if (orderItem.isPrecut) {
          // First check if item has its own precut quantity
          if (orderItem.meta?.jiffy?.precutQuantity !== undefined) {
            itemPrecutQty = orderItem.meta.jiffy.precutQuantity;
          } 
          // Otherwise use order-level precut quantity
          else if (orderPrecutQty > 0) {
            itemPrecutQty = orderPrecutQty;
          }
          // If no precut quantity found but item is marked as precut, default to item qty
          else {
            itemPrecutQty = orderItem.qty || 1;
          }
        }

        // Return item with precutQty added
        return {
          ...orderItem,
          precutQty: itemPrecutQty,
          // Ensure qty is the line item quantity, not precut quantity
          qty: orderItem.qty || 1
        };
      });
    }
  }

  return { json: output };
});

// Process Shipping Label Attachment
// This node should run after the Airtable - Upsert Order node

const items = $input.all();
const parsedData = $('Function - Parse Jiffy').all();

return items.map((item, index) => {
  const orderRecordId = item.json.id; // Airtable record ID from upsert
  const orderData = parsedData[index]?.json?.order || {};
  const attachmentData = orderData._attachments || {};

  // Skip if no shipping label URL
  if (!attachmentData.shippingLabelUrl || !orderRecordId) {
    return {
      json: {
        skip: true,
        reason: !orderRecordId ? 'No order record ID' : 'No shipping label URL',
        orderId: orderData.orderId,
        orderRecordId: orderRecordId
      }
    };
  }

  // Prepare attachment in Airtable format
  const attachment = {
    url: attachmentData.shippingLabelUrl,
    filename: attachmentData.shippingLabelFilename || `JIFFY-${orderData.jiffyPoNumber || 'order'}-shipping-label.pdf`
  };

  return {
    json: {
      recordId: orderRecordId,
      attachments: [attachment],
      orderId: orderData.orderId,
      source: orderData.source,
      debug: {
        hasUrl: !!attachmentData.shippingLabelUrl,
        filename: attachment.filename
      }
    }
  };
});

