={{ 
  (() => {
    const dim = ($json.dimensions || '').toString().trim();

    // Return empty for non-dimensional content
    if (!dim || 
        dim.toLowerCase().includes('sample') || 
        dim.toLowerCase().includes('pack') ||
        dim.toLowerCase().includes('transfer') ||
        dim.toLowerCase().includes('clothing') ||
        dim.toLowerCase().includes('|') ||
        dim.toLowerCase().includes('dtf') ||
        dim.toLowerCase().includes('uv') ||
        dim.toLowerCase().includes('sticker')) {
      return '';
    }

    // Clean up any parenthetical content first
    let cleanDim = dim.replace(/\s*\([^)]*\)/g, '').trim();

    // Already formatted with 'x' (like "22 x 100", "22x100", "22 X 100")
    if (cleanDim.toLowerCase().includes('x')) {
      const parts = cleanDim.split(/\s*[xX]\s*/);
      if (parts.length === 2 && parts[0].match(/^\d+$/) && parts[1].match(/^\d+$/)) {
        return parts[0] + ' x ' + parts[1];
      }
      return cleanDim; // Return as-is if already has x but doesn't match expected format
    }

    // Concatenated format (like "22100", "12050")
    if (/^\d{4,5}$/.test(cleanDim)) {
      // For 4-5 digit numbers, split at position 2
      const width = cleanDim.slice(0, 2);
      const length = cleanDim.slice(2);
      return width + ' x ' + length;
    }

    // Handle 3-digit numbers (like "224" could be "22 x 4")
    if (/^\d{3}$/.test(cleanDim) && cleanDim.startsWith('22')) {
      const width = cleanDim.slice(0, 2);
      const length = cleanDim.slice(2);
      return width + ' x ' + length;
    }

    // Handle other specific patterns if needed
    // Only return if it looks like dimensions (contains only numbers, spaces, and basic dimension chars)
    if (/^[\d\s.,"-]+$/.test(cleanDim) && cleanDim.length > 0) {
      return cleanDim;
    }

    // Return empty for anything else that doesn't look like dimensions
    return '';
  })()
}}

value.Phone:
={{ (() => {
  const phone = $json.order.customer.phone;
  if (!phone) return '';
  const cleaned = phone.toString().replace(/[^0-9]/g, '');
  if (cleaned.length === 10) {
    return cleaned.replace(/(\d{3})(\d{3})(\d{4})/, '($1) $2-$3');
  } else if (cleaned.length === 11 && cleaned[0] === '1') {
    return cleaned.slice(1).replace(/(\d{3})(\d{3})(\d{4})/, '($1) $2-$3');
  } else {
    return phone;
  }
})() }}
