// Process order status request
const input = $input.first().json;
const { orderId, orderNumber, email } = input.body || input;

// Build filter based on provided input
let filterFormula = '';
if (orderId) {
  filterFormula = `RECORD_ID() = '${orderId}'`;
} else if (orderNumber) {
  filterFormula = `{Order ID} = '${orderNumber}'`;
} else if (email) {
  filterFormula = `{Email} = '${email}'`;
} else {
  return [{
    json: {
      error: 'Please provide orderId, orderNumber, or email'
    }
  }];
}

return [{
  json: {
    filterFormula,
    includeItems: true
  }
}];

// Get order details and fetch items
const orders = $('Find Order').all();

if (orders.length === 0) {
  return [{
    json: {
      error: 'Order not found',
      status: 404
    }
  }];
}

const order = orders[0].json;
const orderItemIds = order.fields?.['Order Items'] || [];

// Build filter for order items
const itemFilter = orderItemIds.length > 0 
  ? `OR(${orderItemIds.map(id => `RECORD_ID() = '${id}'`).join(', ')})`
  : '';

return [{
  json: {
    order: {
      id: order.id,
      orderNumber: order.fields?.['Order ID'],
      status: order.fields?.['Order Status'],
      customer: order.fields?.['Customer Name'],
      email: order.fields?.['Email'],
      orderDate: order.fields?.['Order Date'],
      total: order.fields?.['Total $'],
      shippingMethod: order.fields?.['Shipping Method'],
      rushService: order.fields?.['Rush Service'],
      productionProgress: order.fields?.['Production Progress'],
      internalNotes: order.fields?.['Internal Notes']
    },
    itemFilter
  }
}];

// Get production status for each item
const orderData = $('Process Order Data').first().json;
const items = $('Get Order Items').all();

// Get production IDs
const productionIds = [];
items.forEach(item => {
  const prodIds = item.json.fields?.['Linked Production'] || [];
  productionIds.push(...prodIds);
});

// Build filter for productions
const productionFilter = productionIds.length > 0
  ? `OR(${productionIds.map(id => `RECORD_ID() = '${id}'`).join(', ')})`
  : '';

return [{
  json: {
    orderData,
    items: items.map(item => ({
      id: item.json.id,
      name: item.json.fields?.['Title'],
      quantity: item.json.fields?.['Quantity'],
      status: item.json.fields?.['Item Status'],
      productType: item.json.fields?.['Product Type'],
      hasFile: !item.json.fields?.['Needs File'],
      batchName: item.json.fields?.['Batch Name']?.[0]
    })),
    productionFilter
  }
}];

// Compile final response
const baseData = $('Prepare Production Lookup').first().json;
const productions = $('Get Productions').all();

// Map production data to items
const productionMap = new Map();
productions.forEach(prod => {
  productionMap.set(prod.json.id, {
    id: prod.json.fields?.['Production ID'],
    status: prod.json.fields?.['Production Status'],
    progress: prod.json.fields?.['Stage Progress'] || 0,
    stages: {
      printStarted: prod.json.fields?.['Print Started'],
      printCompleted: prod.json.fields?.['Print Completed'],
      qcCompleted: prod.json.fields?.['QC Completed'],
      cutCompleted: prod.json.fields?.['Cut Completed'],
      shipCompleted: prod.json.fields?.['Ship Completed']
    },
    isOverdue: prod.json.fields?.['Is Overdue']
  });
});

// Enhance items with production data
const enhancedItems = baseData.items.map(item => {
  // For now, just include the item data
  // In real implementation, match productions to items
  return {
    ...item,
    currentStage: determineCurrentStage(item.status),
    stageProgress: getStageProgress(item.status)
  };
});

function determineCurrentStage(status) {
  const stageMap = {
    'Pending': 'Awaiting Production',
    'Ready for Production': 'In Queue',
    'In Production': 'Printing',
    'Quality Check': 'QC',
    'Ready to Ship': 'Packing',
    'Shipped': 'Completed'
  };
  return stageMap[status] || status;
}

function getStageProgress(status) {
  const progressMap = {
    'Pending': 0,
    'Ready for Production': 10,
    'In Production': 25,
    'Quality Check': 50,
    'Ready to Ship': 90,
    'Shipped': 100
  };
  return progressMap[status] || 0;
}

// Calculate overall progress
const totalProgress = enhancedItems.length > 0
  ? Math.round(enhancedItems.reduce((sum, item) => sum + item.stageProgress, 0) / enhancedItems.length)
  : 0;

return [{
  json: {
    success: true,
    order: {
      ...baseData.orderData.order,
      overallProgress: totalProgress,
      itemCount: enhancedItems.length,
      completedCount: enhancedItems.filter(i => i.status === 'Shipped').length
    },
    items: enhancedItems,
    summary: {
      total: enhancedItems.length,
      pending: enhancedItems.filter(i => i.status === 'Pending').length,
      inProduction: enhancedItems.filter(i => ['In Production', 'Quality Check'].includes(i.status)).length,
      readyToShip: enhancedItems.filter(i => i.status === 'Ready to Ship').length,
      shipped: enhancedItems.filter(i => i.status === 'Shipped').length
    }
  }
}];

// Get orders ready for pickup
// An order is ready when all items are completed but not yet picked up

const today = new Date().toISOString().split('T')[0];

// Filter for orders that are completed but not picked up
const filterFormula = `AND(
  {Order Status} = 'Completed',
  {Pickup Confirmed} = BLANK(),
  {Production Progress} >= 100
)`;

return [{
  json: {
    filterFormula,
    requestDate: today
  }
}];

// Format pickup ready list
const orders = $('Get Ready Orders').all();

const pickupList = orders.map(order => {
  const fields = order.json.fields;

  // Calculate days waiting
  const completedDate = fields['Status Changed At'];
  const daysWaiting = completedDate 
    ? Math.floor((Date.now() - new Date(completedDate).getTime()) / (1000 * 60 * 60 * 24))
    : 0;

  return {
    orderId: order.json.id,
    orderNumber: fields['Order ID'],
    customerName: fields['Customer Name'],
    email: fields['Email'],
    phone: fields['Phone'],
    itemCount: fields['Order Items']?.length || 0,
    orderTotal: fields['Total $'],
    completedDate: completedDate,
    daysWaiting,
    rushOrder: fields['Rush Service'] === 'Rush',
    shippingMethod: fields['Shipping Method'],
    pickupCode: fields['Pickup Code'] || `PU-${order.json.id.slice(-4)}`,
    notes: fields['Internal Notes'],
    priority: daysWaiting > 3 ? 'high' : fields['Rush Service'] === 'Rush' ? 'high' : 'normal'
  };
});

// Sort by priority and days waiting
pickupList.sort((a, b) => {
  if (a.priority !== b.priority) {
    return a.priority === 'high' ? -1 : 1;
  }
  return b.daysWaiting - a.daysWaiting;
});

// Group by priority
const grouped = {
  urgent: pickupList.filter(o => o.daysWaiting > 5),
  high: pickupList.filter(o => o.priority === 'high' && o.daysWaiting <= 5),
  normal: pickupList.filter(o => o.priority === 'normal')
};

return [{
  json: {
    success: true,
    totalOrders: pickupList.length,
    summary: {
      urgent: grouped.urgent.length,
      high: grouped.high.length,
      normal: grouped.normal.length
    },
    orders: pickupList,
    grouped
  }
}];

// Process pickup confirmation
const input = $input.first().json;
const { orderId, pickupCode, signature, confirmedBy, notes, photo } = input.body || input;

// Validate required fields
if (!orderId || !pickupCode) {
  return [{
    json: {
      success: false,
      error: 'Missing required fields: orderId and pickupCode'
    }
  }];
}

// Prepare order update
const now = new Date().toISOString();
const updateData = {
  'Pickup Confirmed': now,
  'Order Status': 'Picked Up'
};

// Add notes if provided
if (notes) {
  updateData['Internal Notes'] = `Pickup confirmed at ${now} by ${confirmedBy || 'Customer'}. ${notes}`;
}

return [{
  json: {
    orderId,
    updateData,
    pickupLogData: {
      orderId,
      pickupCode,
      confirmedBy: confirmedBy || 'Customer',
      signature,
      photo,
      notes,
      timestamp: now
    }
  }
}];

// Create pickup log entry
const pickupData = $('Process Pickup Confirmation').first().json.pickupLogData;
const orderUpdate = $('Update Order Pickup').first().json;

// Note: In production, you'd create this in a Pickup Log table
// For now, we'll just return success

return [{
  json: {
    success: true,
    message: 'Pickup confirmed successfully',
    orderId: pickupData.orderId,
    pickupTime: pickupData.timestamp,
    confirmedBy: pickupData.confirmedBy
  }
}];

// Process reprint request
const input = $input.first().json;
const { 
  productionId, 
  reason, 
  defectType, 
  photo, 
  priority = 'high',
  requestedBy,
  notes 
} = input.body || input;

// Validate required fields
if (!productionId || !reason) {
  return [{
    json: {
      success: false,
      error: 'Missing required fields: productionId and reason'
    }
  }];
}

// Valid reasons and defect types
const validReasons = ['quality_issue', 'damage', 'wrong_item', 'customer_request', 'other'];
const validDefectTypes = ['print_quality', 'color_mismatch', 'alignment', 'material_defect', 'cutting_error'];

if (!validReasons.includes(reason)) {
  return [{
    json: {
      success: false,
      error: `Invalid reason. Must be one of: ${validReasons.join(', ')}`
    }
  }];
}

return [{
  json: {
    productionId,
    reason,
    defectType: defectType || [],
    priority,
    requestedBy: requestedBy || 'System',
    notes,
    timestamp: new Date().toISOString(),
    needsPhoto: !!photo
  }
}];

// Create reprint production record
const productions = $('Find Production for Reprint').all();
const reprintRequest = $('Process Reprint Request').first().json;

if (productions.length === 0) {
  return [{
    json: {
      success: false,
      error: 'Production record not found'
    }
  }];
}

const originalProduction = productions[0].json;
const fields = originalProduction.fields;

// Create new production record for reprint
const reprintData = {
  'Order Item': fields['Order Item'],
  'Linked Order': fields['Linked Order'],
  'Production Status': 'Pending',
  'Priority': reprintRequest.priority === 'urgent' ? 'Urgent' : 'High',
  'Needs Reprint': false,
  'Reprint Count': (fields['Reprint Count'] || 0) + 1,
  'Hold Reason': `Reprint requested: ${reprintRequest.reason}. Original: ${reprintRequest.productionId}`,
  'QC Status': 'Pending',
  'Stage Progress': 0
};

// Update original production
const originalUpdate = {
  id: originalProduction.id,
  fields: {
    'Production Status': 'Reprint Requested',
    'Needs Reprint': true,
    'Issue Notes': `${reprintRequest.reason}: ${reprintRequest.notes || 'No additional notes'}`
  }
};

return [{
  json: {
    reprintData,
    originalUpdate,
    originalItemId: fields['Order Item']?.[0],
    batchId: fields['Linked Batch']?.[0]
  }
}];

// Get queue status for facility
const input = $input.first().json;
const { facility = 'all' } = input.body || input;

// Build filter for active batches
let filterFormula = `AND(
  OR(
    {Batch Status} = 'Queued',
    {Batch Status} = 'In Production',
    {Batch Status} = 'Printing'
  ),
  {Batch Status} != 'Completed',
  {Batch Status} != 'Cancelled'
)`;

// Add facility filter if specified
if (facility !== 'all') {
  filterFormula = `AND(
    ${filterFormula},
    {Facility} = '${facility}'
  )`;
}

return [{
  json: {
    filterFormula,
    facility
  }
}];

// Format queue status response
const batches = $('Get Queue Batches').all();
const facility = $('Prepare Queue Query').first().json.facility;

// Process batch data
const queueData = batches.map((batch, index) => {
  const fields = batch.json.fields;

  // Calculate estimated wait time based on position
  const avgBatchTime = 2; // hours per batch (adjust based on your data)
  const estimatedStartHours = index * avgBatchTime;
  const estimatedStart = new Date(Date.now() + estimatedStartHours * 60 * 60 * 1000);

  return {
    batchId: batch.json.id,
    batchName: fields['Batch Name'],
    facility: fields['Facility'],
    status: fields['Batch Status'],
    priority: fields['Priority'],
    priorityScore: fields['Dynamic Priority Score'],
    queuePosition: fields['Queue Position'] || index + 1,
    printType: fields['Print Type'],
    totalQuantity: fields['Total Quantity'],
    totalItems: fields['Total Items'],
    orderCount: fields['Total Orders'],
    estimatedStart: estimatedStart.toISOString(),
    estimatedHoursToStart: estimatedStartHours,
    isOverdue: fields['Is Overdue'],
    createdAt: fields['Created'],
    mixedProducts: fields['Mixed Product Types']
  };
});

// Group by status
const grouped = {
  queued: queueData.filter(b => b.status === 'Queued'),
  inProduction: queueData.filter(b => ['In Production', 'Printing'].includes(b.status)),
  upcoming: queueData.slice(0, 5) // Next 5 batches
};

// Calculate summary statistics
const summary = {
  totalBatches: queueData.length,
  queuedBatches: grouped.queued.length,
  activeBatches: grouped.inProduction.length,
  estimatedQueueTime: queueData.length * avgBatchTime,
  urgentBatches: queueData.filter(b => b.priority === 'Urgent').length,
  overdueBatches: queueData.filter(b => b.isOverdue).length
};

return [{
  json: {
    success: true,
    facility,
    timestamp: new Date().toISOString(),
    summary,
    queue: queueData,
    grouped,
    nextBatch: queueData[0] || null
  }
}];

// Get orders ready to ship
const filterFormula = `AND(
  OR(
    {Order Status} = 'Ready to Ship',
    {Order Status} = 'Completed'
  ),
  {Tracking Number} = BLANK(),
  {Production Progress} >= 100
)`;

return [{
  json: {
    filterFormula
  }
}];

// Format shipping queue
const orders = $('Get Shipping Orders').all();

const shippingQueue = orders.map(order => {
  const fields = order.json.fields;

  // Calculate days since order
  const orderDate = new Date(fields['Order Date']);
  const daysSinceOrder = Math.floor((Date.now() - orderDate.getTime()) / (1000 * 60 * 60 * 24));

  return {
    orderId: order.json.id,
    orderNumber: fields['Order ID'],
    customerName: fields['Customer Name'],
    shippingAddress: {
      street: fields['Shipping Street'],
      city: fields['Shipping City'],
      state: fields['Shipping State'],
      zip: fields['Shipping Zip'],
      country: fields['Shipping Country'] || 'US'
    },
    shippingMethod: fields['Shipping Method'],
    rushService: fields['Rush Service'] === 'Rush',
    itemCount: fields['Order Items']?.length || 0,
    orderTotal: fields['Total $'],
    daysSinceOrder,
    priority: fields['Rush Service'] === 'Rush' ? 'high' : daysSinceOrder > 5 ? 'high' : 'normal',
    weight: estimateWeight(fields['Order Items']?.length || 0),
    packageType: 'standard'
  };
});

// Group by shipping method
const grouped = {};
shippingQueue.forEach(order => {
  const method = order.shippingMethod || 'Standard';
  if (!grouped[method]) grouped[method] = [];
  grouped[method].push(order);
});

// Helper function to estimate weight
function estimateWeight(itemCount) {
  return (itemCount * 0.5 + 0.5).toFixed(1);
}

return [{
  json: {
    success: true,
    totalOrders: shippingQueue.length,
    orders: shippingQueue,
    groupedByMethod: grouped,
    summary: {
      standard: grouped['Standard']?.length || 0,
      express: grouped['Express']?.length || 0,
      overnight: grouped['Overnight']?.length || 0,
      local: grouped['Local Delivery']?.length || 0
    }
  }
}];

// Get production metrics
const input = $input.first().json;
const { dateRange = 'today', facility = 'all' } = input.body || input;

// Calculate date filter
let dateFilter = '';

switch(dateRange) {
  case 'today':
    dateFilter = `IS_SAME({Created}, TODAY(), 'day')`;
    break;
  case 'week':
    dateFilter = `{Created} >= DATEADD(TODAY(), -7, 'days')`;
    break;
  case 'month':
    dateFilter = `{Created} >= DATEADD(TODAY(), -30, 'days')`;
    break;
  default:
    dateFilter = `IS_SAME({Created}, TODAY(), 'day')`;
}

// Build filter
let filterFormula = dateFilter;
if (facility !== 'all') {
  filterFormula = `AND(${dateFilter}, {Printer Used} = '${facility}')`;
}

return [{
  json: {
    filterFormula,
    dateRange,
    facility
  }
}];

// Calculate production metrics
const productions = $('Get Production Records').all();
const query = $('Prepare Metrics Query').first().json;

// Initialize metrics
const metrics = {
  totalItems: productions.length,
  completed: 0,
  inProgress: 0,
  pending: 0,
  defects: 0,
  reprints: 0,
  overdue: 0,
  avgCycleTime: 0,
  onTimeRate: 0,
  defectRate: 0,
  byStatus: {},
  byPriority: {},
  hourlyProduction: {}
};

// Process each production record
let totalCycleTime = 0;
let completedWithTime = 0;

productions.forEach(prod => {
  const fields = prod.json.fields;
  const status = fields['Production Status'];

  // Count by status
  metrics.byStatus[status] = (metrics.byStatus[status] || 0) + 1;

  if (status === 'Completed') {
    metrics.completed++;
  } else if (['Printing', 'Quality Check', 'Cutting'].includes(status)) {
    metrics.inProgress++;
  } else if (status === 'Pending') {
    metrics.pending++;
  }

  // Count defects and reprints
  if (fields['Defect Found']) metrics.defects++;
  if (fields['Needs Reprint']) metrics.reprints++;
  if (fields['Is Overdue']) metrics.overdue++;

  // Count by priority
  const priority = fields['Priority'] || 'Standard';
  metrics.byPriority[priority] = (metrics.byPriority[priority] || 0) + 1;

  // Calculate cycle time for completed items
  if (fields['Print Started'] && fields['Ship Completed']) {
    const cycleTime = (new Date(fields['Ship Completed']) - new Date(fields['Print Started'])) / (1000 * 60 * 60);
    totalCycleTime += cycleTime;
    completedWithTime++;
  }

  // Track hourly production
  if (fields['Ship Completed']) {
    const hour = new Date(fields['Ship Completed']).getHours();
    metrics.hourlyProduction[hour] = (metrics.hourlyProduction[hour] || 0) + 1;
  }
});

// Calculate rates
if (completedWithTime > 0) {
  metrics.avgCycleTime = (totalCycleTime / completedWithTime).toFixed(1);
}

if (metrics.totalItems > 0) {
  metrics.defectRate = ((metrics.defects / metrics.totalItems) * 100).toFixed(1);
  metrics.onTimeRate = (((metrics.totalItems - metrics.overdue) / metrics.totalItems) * 100).toFixed(1);
}

// Calculate throughput
const throughput = {
  itemsPerHour: query.dateRange === 'today' 
    ? (metrics.completed / (new Date().getHours() || 1)).toFixed(1)
    : (metrics.completed / 24).toFixed(1),
  utilizationRate: ((metrics.inProgress / 10) * 100).toFixed(1)
};

return [{
  json: {
    success: true,
    dateRange: query.dateRange,
    facility: query.facility,
    timestamp: new Date().toISOString(),
    metrics: {
      ...metrics,
      throughput
    },
    summary: {
      totalProduction: metrics.totalItems,
      completionRate: metrics.totalItems > 0 ? ((metrics.completed / metrics.totalItems) * 100).toFixed(1) : 0,
      qualityRate: metrics.totalItems > 0 ? (((metrics.totalItems - metrics.defects) / metrics.totalItems) * 100).toFixed(1) : 0,
      efficiency: throughput.utilizationRate
    }
  }
}];

// Update batch priority
const input = $input.first().json;
const { batchId, newPriority, reason } = input.body || input;

// Validate inputs
if (!batchId || !newPriority) {
  return [{
    json: {
      success: false,
      error: 'Missing required fields: batchId and newPriority'
    }
  }];
}

const validPriorities = ['Standard', 'High', 'Urgent'];
if (!validPriorities.includes(newPriority)) {
  return [{
    json: {
      success: false,
      error: `Invalid priority. Must be one of: ${validPriorities.join(', ')}`
    }
  }];
}

// Calculate new priority score
const priorityScores = {
  'Standard': 1,
  'High': 5,
  'Urgent': 10
};

const updateData = {
  'Priority': newPriority,
  'Dynamic Priority Score': priorityScores[newPriority] * 100
};

if (reason) {
  updateData['Internal Notes'] = `Priority changed to ${newPriority}: ${reason}`;
}

return [{
  json: {
    batchId,
    updateData
  }
}];

// Return success response
const updateResult = $('Update Batch Priority').first().json;

return [{
  json: {
    success: true,
    message: 'Priority updated successfully',
    batchId: updateResult.id,
    newPriority: updateResult.fields?.Priority
  }
}];

// Enhanced Production Stage Handler
// Place this in your "Process Stage Scan v2" node

const input = $input.first().json;
const { qrCode, productionId, stage, action = 'complete', operator, notes, printer } = input.body || input;

// Handle both QR scan and manual input
let prodId = null;
if (qrCode && qrCode.startsWith('PROD:')) {
  prodId = qrCode.replace('PROD:', '');
} else if (productionId) {
  prodId = productionId;
} else {
  throw new Error('No production ID provided');
}

// Current timestamp
const now = new Date().toISOString();

// Stage configuration matching your schema
const stageUpdates = {
  'print': {
    'start': {
      fields: { 
        'Print Started': now, 
        'Production Status': 'Printing',
        'Printer Used': printer || null 
      },
      progress: 0,
      itemStatus: 'In Production'
    },
    'complete': {
      fields: { 'Print Completed': now, 'Production Status': 'Print Complete' },
      progress: 25,
      itemStatus: null
    }
  },
  'qc': {
    'complete': {
      fields: { 'QC Completed': now, 'QC Status': 'Passed', 'Production Status': 'QC Complete' },
      progress: 50,
      itemStatus: 'Quality Check'
    }
  },
  'cut': {
    'complete': {
      fields: { 'Cut Completed': now, 'Production Status': 'Cut Complete' },
      progress: 75,
      itemStatus: null
    }
  },
  'ship': {
    'complete': {
      fields: { 'Ship Completed': now, 'Production Status': 'Completed' },
      progress: 100,
      itemStatus: 'Ready to Ship'
    }
  }
};

// Get update configuration
const update = stageUpdates[stage.toLowerCase()]?.[action];
if (!update) {
  throw new Error(`Invalid stage/action: ${stage}/${action}`);
}

// Build scan history entry
const scanEntry = `${now} - ${stage} ${action} by ${operator || 'System'}${printer ? ` on ${printer}` : ''}${notes ? ` - ${notes}` : ''}`;

// Build filter to find record
const filterFormula = `{Production ID} = '${prodId}'`;

return [{
  json: {
    filterFormula,
    updateFields: {
      ...update.fields,
      'Stage Progress': update.progress,
      'Operator': operator,
      'Last Modified': now
    },
    scanHistoryEntry: scanEntry,
    itemStatus: update.itemStatus,
    stage: stage.toLowerCase(),
    action
  }
}];

// Update Production Record and Scan History
const records = $('Find Production Record').all();
const scanData = $('Process Stage Scan v2').first().json;

if (records.length === 0) {
  return [{
    json: {
      error: 'No production record found',
      filter: scanData.filterFormula
    }
  }];
}

const record = records[0].json;
const recordId = record.id;

// Get existing scan history
let scanHistory = record.fields?.['Scan History'] || '';
if (scanHistory) {
  scanHistory += '\n' + scanData.scanHistoryEntry;
} else {
  scanHistory = scanData.scanHistoryEntry;
}

// Add scan history to update
const updateFields = {
  ...scanData.updateData,
  'Scan History': scanHistory
};

// Check if this completes the item
const orderItemId = record.fields?.['Order Item']?.[0];
let newItemStatus = null;

if (scanData.stage === 'print' && scanData.action === 'start') {
  newItemStatus = 'In Production';
} else if (scanData.stage === 'qc' && scanData.action === 'complete') {
  newItemStatus = 'Quality Check';
} else if (scanData.stage === 'ship' && scanData.action === 'complete') {
  newItemStatus = 'Ready to Ship';
}

return [{
  json: {
    recordId,
    updateFields,
    orderItemId,
    newItemStatus,
    batchId: record.fields?.['Linked Batch']?.[0]
  }
}];

// Process QC Results
const input = $input.first().json;
const { productionId, qcStatus, defectFound, defectType, issueNotes, needsReprint, operator } = input.body || input;

// Validate inputs
if (!productionId || !qcStatus) {
  return [{
    json: {
      error: 'Missing required fields: productionId, qcStatus',
      received: input
    }
  }];
}

// Build update data
const updateData = {
  'QC Status': qcStatus, // Should be: Passed, Failed, Partial Pass
  'QC Completed': new Date().toISOString(),
  'Defect Found': defectFound || false,
  'Stage Progress': 50, // QC complete = 50%
  'Last Modified': new Date().toISOString()
};

// Add optional fields
if (defectType) updateData['Defect Type'] = defectType;
if (issueNotes) updateData['Issue Notes'] = issueNotes;
if (needsReprint !== undefined) updateData['Needs Reprint'] = needsReprint;
if (operator) updateData['Operator'] = operator;

// Update production status based on QC result
if (qcStatus === 'Failed' && needsReprint) {
  updateData['Production Status'] = 'Needs Reprint';
} else if (qcStatus === 'Passed') {
  updateData['Production Status'] = 'Quality Check';
}

// Add to scan history
const scanEntry = `${new Date().toISOString()} - QC ${qcStatus} by ${operator || 'Unknown'}${defectFound ? ` (Defect: ${defectType})` : ''}`;

return [{
  json: {
    filterFormula: `RECORD_ID() = '${productionId}'`,
    updateData,
    scanHistoryEntry: scanEntry,
    needsReprintBatch: qcStatus === 'Failed' && needsReprint
  }
}];

// Prepare QC Update for Production Record
const qcData = $('Process QC Update').first().json;
const records = $('Find Production Record').all();

if (records.length === 0) {
  return [{
    json: {
      error: 'No production record found',
      productionId: qcData.productionId
    }
  }];
}

const record = records[0].json;
const recordId = record.id;

// Get existing values
let scanHistory = record.fields?.['Scan History'] || '';
let reprintCount = record.fields?.['Reprint Count'] || 0;

// Add to scan history
if (scanHistory) {
  scanHistory += '\n' + qcData.scanHistoryEntry;
} else {
  scanHistory = qcData.scanHistoryEntry;
}

// Increment reprint count if needed
if (qcData.needsReprintBatch && record.fields?.['Needs Reprint']) {
  reprintCount++;
}

// Merge QC update data with scan history
const updateFields = {
  ...qcData.updateData,
  'Scan History': scanHistory,
  'Reprint Count': reprintCount
};

return [{
  json: {
    recordId,
    updateFields,
    needsReprintBatch: qcData.needsReprintBatch
  }
}];

// Process validation request
const input = $input.first().json;
const { productionId, facility } = input.body || input;

// Validate inputs
if (!productionId || !facility) {
  return [{
    json: {
      valid: false,
      message: 'Missing production ID or facility'
    }
  }];
}

// Validate facility name
const validFacilities = ['CHICO', 'OUTPOST'];
if (!validFacilities.includes(facility.toUpperCase())) {
  return [{
    json: {
      valid: false,
      message: `Invalid facility. Must be CHICO or OUTPOST`
    }
  }];
}

// Build filter to find production record
// Handle both formats: 'P093' or 'PROD:P093'
const cleanId = productionId.replace('PROD:', '');
const filterFormula = `{Production ID} = '${cleanId}'`;

return [{
  json: {
    filterFormula,
    facility: facility.toUpperCase(),
    productionId: cleanId,
    originalInput: productionId
  }
}];

// Check if production record was found
const productionRecords = $('Find Production Record1').all();
const requestData = $('Process Validation Request').first().json;

if (productionRecords.length === 0) {
  return [{
    json: {
      valid: false,
      message: `Production record ${requestData.productionId} not found`,
      productionId: requestData.productionId
    }
  }];
}

const production = productionRecords[0].json;
const batchId = production.fields?.['Linked Batch']?.[0];

if (!batchId) {
  return [{
    json: {
      valid: false,
      message: 'Production record has no assigned batch',
      productionId: requestData.productionId
    }
  }];
}

// Continue to fetch batch details
return [{
  json: {
    batchId,
    filterFormula: `RECORD_ID() = '${batchId}'`,
    facility: requestData.facility,
    productionId: requestData.productionId,
    productionStatus: production.fields?.['Production Status']
  }
}];

// Validate facility assignment
const batchRecords = $('Get Batch Record').all();
const validationData = $('Check Production Exists').first().json;

if (batchRecords.length === 0) {
  return [{
    json: {
      valid: false,
      message: 'Batch record not found',
      batchId: validationData.batchId
    }
  }];
}

const batch = batchRecords[0].json;
const batchFacility = batch.fields?.['Facility'];
const requestedFacility = validationData.facility;

// Check if facility matches
const valid = batchFacility === requestedFacility;

return [{
  json: {
    valid,
    message: valid 
      ? 'Valid assignment' 
      : `This item is assigned to ${batchFacility || 'no facility'}, not ${requestedFacility}`,
    productionId: validationData.productionId,
    batchName: batch.fields?.['Batch Name'],
    batchFacility,
    requestedFacility,
    batchStatus: batch.fields?.['Batch Status'],
    productionStatus: validationData.productionStatus
  }
}];
