// Extract facility from request
const input = $input.first().json;
const facility = input.body?.facility || 'ALL';

// Build filter formulas for each metric with correct field mappings
const queries = {
  activeOrders: facility === 'ALL'
    ? "AND({Order Status} != 'seltnfE3r8HcApXwG', {Order Status} != 'sellv2KvP6JuoZaj6', {Order Status} != 'selwjoXFHOJ7JZZIM')"
    : `AND({Order Status} != 'seltnfE3r8HcApXwG', {Order Status} != 'sellv2KvP6JuoZaj6', {Order Status} != 'selwjoXFHOJ7JZZIM', {Print Facility} = '${facility}')`,

  inProduction: facility === 'ALL'
    ? "OR({Production Status} = 'sel6f7v4gN9GPqgxt', {Production Status} = 'selDaG1p0sQ2kO7HI', {Production Status} = 'selPaWyNohscJYysh')"
    : `AND(OR({Production Status} = 'sel6f7v4gN9GPqgxt', {Production Status} = 'selDaG1p0sQ2kO7HI', {Production Status} = 'selPaWyNohscJYysh'), {Print Facility} = '${facility}')`,

  readyPickup: facility === 'ALL'
    ? "AND({Order Status} = 'selgEsxegmq7yufti', {Fulfillment Option} = 'selaxl7C9OF2nnzDS')"
    : `AND({Order Status} = 'selgEsxegmq7yufti', {Fulfillment Option} = 'selaxl7C9OF2nnzDS', {Print Facility} = '${facility}')`,

  queueItems: facility === 'ALL'
    ? "OR({Batch Status} = 'selAWIgrGVJa72pEV', {Batch Status} = 'selw7nDzeWs0a8z6N')"
    : `AND(OR({Batch Status} = 'selAWIgrGVJa72pEV', {Batch Status} = 'selw7nDzeWs0a8z6N'), {Print Facility} = '${facility}')`
};

return [
  { json: { queryType: 'activeOrders', formula: queries.activeOrders, table: 'Orders', facility }},
  { json: { queryType: 'inProduction', formula: queries.inProduction, table: 'Productions', facility }},
  { json: { queryType: 'readyPickup', formula: queries.readyPickup, table: 'Orders', facility }},
  { json: { queryType: 'queueItems', formula: queries.queueItems, table: 'Batches', facility }}
];

// Get results from all Airtable queries
const activeOrdersData = $('Count Active Orders').all();
const inProductionData = $('Count In Production').all();
const readyPickupData = $('Count Ready Pickup').all();
const queueItemsData = $('Count Queue Items').all();

// Count records from each query
const activeOrders = activeOrdersData.length > 0 ? activeOrdersData.length : 0;
const inProduction = inProductionData.length > 0 ? inProductionData.length : 0;
const readyPickup = readyPickupData.length > 0 ? readyPickupData.length : 0;
const queueItems = queueItemsData.length > 0 ? queueItemsData.length : 0;

// Get facility from original request
const facility = $('Parse Request & Build Queries').first().json.facility || 'ALL';

return [{
  json: {
    success: true,
    stats: {
      activeOrders,
      inProduction,
      readyPickup,
      queueItems,
      lastUpdated: new Date().toISOString(),
      facility
    }
  }
}];
